        }
      },
      endpoints: {
        '/api/proxy/site': 'Website proxy with full HTML processing',
        '/api/proxy/image': 'Image proxy with optimization',
        '/api/proxy/video': 'Video streaming proxy',
        '/api/proxy/document': 'PDF and document viewer',
