import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

server/youtube.ts.content:
interface YouTubeVideo {
  id: string;
  title: string;
  description: string;
  thumbnail: string;
  duration: string;
  views: number;
  publishedAt: string;
  channelTitle: string;
  channelId: string;
}

interface YouTubeChannel {
  id: string;
  title: string;
  description: string;
  thumbnail: string;
  subscriberCount: number;
}

class YouTubeService {
  private apiKey: string;
  private baseUrl = 'https://www.googleapis.com/youtube/v3';

  constructor() {
    this.apiKey = process.env.YOUTUBE_API_KEY || '';
  }

  private async makeRequest(endpoint: string, params: Record<string, string>) {
    if (!this.apiKey) {
      throw new Error('YouTube API key not configured');
    }

    const url = new URL(`${this.baseUrl}/${endpoint}`);
    url.searchParams.append('key', this.apiKey);

    Object.entries(params).forEach(([key, value]) => {
      url.searchParams.append(key, value);
    });

    const response = await fetch(url.toString());
    if (!response.ok) {
      const errorData = await response.text();
      throw new Error(`YouTube API error: ${response.status} ${response.statusText} - ${errorData}`);
    }

    return response.json();
  }

  private parseDuration(duration: string): string {
    const match = duration.match(/PT(?:(\d+)H)?(?:(\d+)M)?(?:(\d+)S)?/);
    if (!match) return '0:00';

    const hours = parseInt(match[1] || '0');
    const minutes = parseInt(match[2] || '0');
    const seconds = parseInt(match[3] || '0');

    if (hours > 0) {
      return `${hours}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
    }
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  }

  async getPopularVideos(maxResults: number = 25): Promise<YouTubeVideo[]> {
    try {
      const data = await this.makeRequest('videos', {
        part: 'snippet,statistics,contentDetails',
        chart: 'mostPopular',
        regionCode: 'US',
        maxResults: maxResults.toString()
      });

      return data.items.map((item: any) => ({
        id: item.id,
        title: item.snippet.title,
        description: item.snippet.description,
        thumbnail: item.snippet.thumbnails.high?.url || item.snippet.thumbnails.medium?.url,
        duration: this.parseDuration(item.contentDetails.duration),
        views: parseInt(item.statistics.viewCount || '0'),
        publishedAt: item.snippet.publishedAt,
        channelTitle: item.snippet.channelTitle,
        channelId: item.snippet.channelId
      }));
    } catch (error) {
      console.error('Error fetching popular videos:', error);
      throw error;
    }
  }

  async searchVideos(query: string, maxResults: number = 25): Promise<YouTubeVideo[]> {
    try {
      const searchData = await this.makeRequest('search', {
        part: 'snippet',
        q: query,
        type: 'video',
        maxResults: maxResults.toString(),
        order: 'relevance'
      });

      const videoIds = searchData.items.map((item: any) => item.id.videoId).join(',');

      const videoData = await this.makeRequest('videos', {
        part: 'snippet,statistics,contentDetails',
        id: videoIds
      });

      return videoData.items.map((item: any) => ({
        id: item.id,
        title: item.snippet.title,
        description: item.snippet.description,
        thumbnail: item.snippet.thumbnails.high?.url || item.snippet.thumbnails.medium?.url,
        duration: this.parseDuration(item.contentDetails.duration),
        views: parseInt(item.statistics.viewCount || '0'),
        publishedAt: item.snippet.publishedAt,
        channelTitle: item.snippet.channelTitle,
        channelId: item.snippet.channelId
      }));
    } catch (error) {
      console.error('Error searching videos:', error);
      throw error;
    }
  }

  async getVideosByCategory(categoryId: string, maxResults: number = 25): Promise<YouTubeVideo[]> {
    try {
      const data = await this.makeRequest('videos', {
        part: 'snippet,statistics,contentDetails',
        chart: 'mostPopular',
        videoCategoryId: categoryId,
        regionCode: 'US',
        maxResults: maxResults.toString()
      });

      return data.items.map((item: any) => ({
        id: item.id,
        title: item.snippet.title,
        description: item.snippet.description,
        thumbnail: item.snippet.thumbnails.high?.url || item.snippet.thumbnails.medium?.url,
        duration: this.parseDuration(item.contentDetails.duration),
        views: parseInt(item.statistics.viewCount || '0'),
        publishedAt: item.snippet.publishedAt,
        channelTitle: item.snippet.channelTitle,
        channelId: item.snippet.channelId
      }));
    } catch (error) {
      console.error('Error fetching category videos:', error);
      throw error;
    }
  }

  async getChannelInfo(channelId: string): Promise<YouTubeChannel> {
    try {
      const data = await this.makeRequest('channels', {
        part: 'snippet,statistics',
        id: channelId
      });

      const channel = data.items[0];
      return {
        id: channel.id,
        title: channel.snippet.title,
        description: channel.snippet.description,
        thumbnail: channel.snippet.thumbnails.high?.url || channel.snippet.thumbnails.medium?.url,
        subscriberCount: parseInt(channel.statistics.subscriberCount || '0')
      };
    } catch (error) {
      console.error('Error fetching channel info:', error);
      throw error;
    }
  }

  getVideoEmbedUrl(videoId: string): string {
    return `https://www.youtube.com/embed/${videoId}`;
  }

  getVideoWatchUrl(videoId: string): string {
    return `https://www.youtube.com/watch?v=${videoId}`;
  }
}

export const youtubeService = new YouTubeService();
export type { YouTubeVideo, YouTubeChannel };

shared/schema.ts.content:
import { pgTable, text, serial, integer, boolean, timestamp } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";
import { relations } from "drizzle-orm";

export const users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const files: any = pgTable("files", {
  id: serial("id").primaryKey(),
  name: text("name").notNull(),
  path: text("path").notNull(),
  type: text("type").notNull(), // 'file' or 'folder'
  content: text("content"),
  size: integer("size").default(0),
  userId: integer("user_id").notNull(),
  parentId: integer("parent_id"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

export const osSettings = pgTable("os_settings", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").references(() => users.id).notNull(),
  theme: text("theme").default("dark").notNull(),
  wallpaper: text("wallpaper").default("default").notNull(),
  showDesktopIcons: boolean("show_desktop_icons").default(true).notNull(),
  soundEffects: boolean("sound_effects").default(true).notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// Relations
export const usersRelations = relations(users, ({ many, one }) => ({
  files: many(files),
  settings: one(osSettings),
}));

export const filesRelations = relations(files, ({ one, many }) => ({
  user: one(users, {
    fields: [files.userId],
    references: [users.id],
  }),
  parent: one(files, {
    fields: [files.parentId],
    references: [files.id],
  }),
  children: many(files),
}));

export const osSettingsRelations = relations(osSettings, ({ one }) => ({
  user: one(users, {
    fields: [osSettings.userId],
    references: [users.id],
  }),
}));

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export const insertFileSchema = createInsertSchema(files).pick({
  name: true,
  path: true,
  type: true,
  content: true,
  size: true,
  userId: true,
  parentId: true,
});

export const insertOsSettingsSchema = createInsertSchema(osSettings).pick({
  userId: true,
  theme: true,
  wallpaper: true,
  showDesktopIcons: true,
  soundEffects: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type InsertFile = z.infer<typeof insertFileSchema>;
export type File = typeof files.$inferSelect;
export type InsertOsSettings = z.infer<typeof insertOsSettingsSchema>;
export type OsSettings = typeof osSettings.$inferSelect;

client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { Desktop } from "@/components/desktop/Desktop";
import { ArchInstaller } from "@/components/ArchInstaller";
import { WelcomeDialog } from "@/components/WelcomeDialog";
import { LoginScreen } from "@/components/LoginScreen";
import { LoginAnimation } from "@/components/LoginAnimation";
import { ShutdownScreen } from "@/components/ShutdownScreen";
import { ShutdownConsole } from "@/components/ShutdownConsole";
import { BumblebeeOS } from "@/components/BumblebeeOS";
import NotFound from "@/pages/not-found";
import { useState } from "react";

function Router() {
  const [isInstalled, setIsInstalled] = useState(() => {
    // Check if we have terminal redirect parameter after factory reset
    const urlParams = new URLSearchParams(window.location.search);
    if (urlParams.get('terminal') === 'true') {
      // Clear the URL parameter
      window.history.replaceState({}, '', window.location.pathname);
      return false; // Force terminal view
    }
    // Check if already installed
    return localStorage.getItem('archInstalled') === 'true';
  });

  const [showWelcome, setShowWelcome] = useState(() => {
    // Check if setup is complete
    return localStorage.getItem('webos-setup-complete') !== 'true';
  });

  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const [showShutdown, setShowShutdown] = useState(false);
  const [showShutdownConsole, setShowShutdownConsole] = useState(false);
  const [shutdownType, setShutdownType] = useState<'shutdown' | 'restart' | 'logout'>('shutdown');
  const [showLoginAnimation, setShowLoginAnimation] = useState(false);
  const [currentUser, setCurrentUser] = useState(() => {
    const stored = localStorage.getItem('webos-current-user');
    return stored ? JSON.parse(stored) : null;
  });

  const [inBumblebeeOS, setInBumblebeeOS] = useState(() => {
    return localStorage.getItem('bumblebee-active') === 'true';
  });

  const handleInstallComplete = () => {
    localStorage.setItem('archInstalled', 'true');
    setIsInstalled(true);
    setShowWelcome(false);
    setIsLoggedIn(false);
    // Clear any auto-login states
    localStorage.removeItem('webos-current-user');
    localStorage.removeItem('webos-auto-login');
  };

  const handleWelcomeComplete = (userInfo: any) => {
    // Apply user settings but don't auto-login
    localStorage.setItem('webos-theme', userInfo.theme);
    localStorage.setItem('webos-wallpaper', userInfo.wallpaper);
    localStorage.setItem('webos-username', userInfo.username);
    localStorage.setItem('webos-setup-complete', 'true');

    // Apply theme immediately
    document.documentElement.classList.toggle('dark', userInfo.theme === 'dark');

    // Dispatch events to update desktop
    window.dispatchEvent(new CustomEvent('wallpaper-change', { 
      detail: { wallpaper: getWallpaperClass(userInfo.wallpaper) } 
    }));

    setShowWelcome(false);
    setIsLoggedIn(false); // Force manual login
  };

  const handleLogin = (user: any) => {
    setCurrentUser(user);
    setShowLoginAnimation(true);

    // Apply user preferences
    document.documentElement.classList.toggle('dark', user.theme === 'dark');
    window.dispatchEvent(new CustomEvent('wallpaper-change', { 
      detail: { wallpaper: getWallpaperClass(user.wallpaper) } 
    }));
  };

  const handleLoginAnimationComplete = () => {
    setShowLoginAnimation(false);
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    setShutdownType('logout');
    setShowShutdown(false);
    setShowShutdownConsole(true);
  };

  const handleRestart = () => {
    setShutdownType('restart');
    setShowShutdown(false);
    setShowShutdownConsole(true);
  };

  const handleShutdown = () => {
    setShowShutdown(true);
  };

  const handleShutdownAction = () => {
    setShutdownType('shutdown');
    setShowShutdown(false);
    setShowShutdownConsole(true);
  };

  const handleShutdownComplete = () => {
    setShowShutdownConsole(false);

    if (shutdownType === 'logout') {
      localStorage.removeItem('webos-current-user');
      localStorage.removeItem('webos-last-user');
      setCurrentUser(null);
      setIsLoggedIn(false);
    } else if (shutdownType === 'restart') {
      localStorage.removeItem('webos-current-user');
      localStorage.removeItem('webos-last-user');
      window.location.reload();
    } else {
      localStorage.removeItem('webos-current-user');
      localStorage.removeItem('webos-last-user');
      setCurrentUser(null);
      setIsLoggedIn(false);
    }
  };

  const handleBumblebeeOSLaunch = () => {
    localStorage.setItem('bumblebee-active', 'true');
    setInBumblebeeOS(true);
  };

  const handleReturnToWebOS = () => {
    localStorage.removeItem('bumblebee-active');
    setInBumblebeeOS(false);
  };

  const handleFactoryReset = () => {
    // Clear all user data except installation status
    const keysToKeep = [
      'archInstalled',
      'webos-setup-complete'
    ];

    const allKeys = Object.keys(localStorage);
    allKeys.forEach(key => {
      if (!keysToKeep.includes(key)) {
        localStorage.removeItem(key);
      }
    });

    // Reset application state
    setCurrentUser(null);
    setIsLoggedIn(false);
    setShowShutdown(false);

    // Force a complete page reload to reset all state
    window.location.reload();
  };

  const getWallpaperClass = (wallpaper: string) => {
    switch (wallpaper) {
      case 'terminal':
        return 'bg-gradient-to-br from-green-900 to-black';
      case 'code':
        return 'bg-gradient-to-br from-blue-900 to-indigo-900';
      case 'matrix':
        return 'bg-gradient-to-br from-black to-green-800';
      case 'cyber':
        return 'bg-gradient-to-br from-purple-900 to-black';
      case 'sunset':
        return 'bg-gradient-to-br from-orange-600 to-red-800';
      case 'ocean':
        return 'bg-gradient-to-br from-blue-400 to-blue-800';
      case 'forest':
        return 'bg-gradient-to-br from-green-600 to-green-900';
      case 'fire':
        return 'bg-gradient-to-br from-red-500 to-orange-700';
      case 'cosmic':
        return 'bg-gradient-to-br from-indigo-600 to-purple-800';
      case 'dawn':
        return 'bg-gradient-to-br from-pink-400 to-purple-600';
      case 'midnight':
        return 'bg-gradient-to-br from-gray-900 to-blue-900';
      case 'custom':
        const customImage = localStorage.getItem('webos-custom-wallpaper');
        return customImage ? `bg-[url('${customImage}')] bg-cover bg-center bg-no-repeat` : 'bg-gradient-to-br from-slate-900 via-slate-800 to-blue-900';
      default:
        return 'bg-gradient-to-br from-slate-900 via-slate-800 to-blue-900';
    }
  };

  if (!isInstalled) {
    return <ArchInstaller onInstallComplete={handleInstallComplete} />;
  }

  if (inBumblebeeOS) {
    return <BumblebeeOS onReturnToWebOS={handleReturnToWebOS} />;
  }

  if (showShutdownConsole) {
    return (
      <ShutdownConsole 
        onComplete={handleShutdownComplete}
        shutdownType={shutdownType}
      />
    );
  }

  if (showShutdown) {
    return (
      <ShutdownScreen 
        onRestart={handleRestart}
        onLogout={handleLogout}
        onCancel={() => setShowShutdown(false)}
        onFactoryReset={handleFactoryReset}
        onShutdown={handleShutdownAction}
      />
    );
  }

  if (showLoginAnimation && currentUser) {
    return (
      <LoginAnimation 
        username={currentUser.fullName || currentUser.username}
        onComplete={handleLoginAnimationComplete}
      />
    );
  }

  if (!isLoggedIn) {
    return (
      <LoginScreen 
        onLogin={handleLogin}
        onShutdown={handleShutdown}
      />
    );
  }

  return (
    <>
      <Switch>
        <Route path="/" component={() => <Desktop 
          onLogout={handleLogout} 
          onShutdown={handleShutdown}
          onRestart={handleRestart}
        />} />
        <Route component={NotFound} />
      </Switch>

      <WelcomeDialog 
        isOpen={showWelcome} 
        onComplete={handleWelcomeComplete} 
      />
    </>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Router />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

