}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/* Custom OS Styles */
.desktop-icon {
  transition: all 0.2s ease;
}

.desktop-icon:hover {
  transform: scale(1.05);
}

/* Corrupted desktop state */
.corrupted-desktop {
  background: linear-gradient(45deg, #ff0000 0%, #000000 50%, #ff0000 100%);
  background-size: 20px 20px;
  animation: corrupted-flicker 2s infinite;
}

@keyframes corrupted-flicker {
  0%, 50%, 100% { opacity: 1; }
  25%, 75% { opacity: 0.8; }
}

.corrupted-wallpaper {
  background: repeating-linear-gradient(
    45deg,
    #220000,
    #220000 10px,
    #440000 10px,
    #440000 20px
  );
  position: relative;
}

.corrupted-wallpaper::before {
  content: "SYSTEM ERROR - MISSING DEPENDENCIES";
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  color: #ff4444;
  font-size: 24px;
  font-weight: bold;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.8);
  animation: error-blink 1s infinite;
}

@keyframes error-blink {
  0%, 50% { opacity: 1; }
  51%, 100% { opacity: 0.3; }
}

.window-shadow {
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.12);
}

.glass-effect {
  backdrop-filter: blur(20px);
  background: rgba(255, 255, 255, 0.8);
}

.taskbar-glass {
  backdrop-filter: blur(30px);
  background: rgba(248, 248, 248, 0.8);
}

.dragging {
  user-select: none;
  z-index: 1000;
}

.window-content {
  height: calc(100% - 40px);
}

.select-none {
  user-select: none;
}



/* Custom animations */
@keyframes fade-in {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes scale-in {
  from { opacity: 0; transform: scale(0.9); }
  to { opacity: 1; transform: scale(1); }
}

@keyframes slide-up {
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
}

.animate-fade-in {
  animation: fade-in 0.2s ease-out;
}

.animate-scale-in {
  animation: scale-in 0.2s ease-out;
}

.animate-slide-up {
  animation: slide-up 0.3s ease-out;
}

/* Scrollbar styling */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: transparent;
}

::-webkit-scrollbar-thumb {
  background: rgba(0, 0, 0, 0.2);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: rgba(0, 0, 0, 0.3);
}

/* Login Animation Styles */
.animate-fade-in {
  animation: fadeIn 1s ease-in-out;
}

@keyframes fadeIn {
  0% {
    opacity: 0;
    transform: translateY(20px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

