export const PrideColors = {
  red: '#ef4444',
  orange: '#f97316', 
  yellow: '#eab308',
  green: '#22c55e',
  blue: '#3b82f6',
  indigo: '#6366f1',
  purple: '#a855f7'
};

export const usePrideTheme = () => {
  const [isEnabled, setIsEnabled] = useState(() => {
    // Check user preference first
    const userDisabled = localStorage.getItem('pride-theme-disabled') === 'true';
    if (userDisabled) return false;

    // Check if manually enabled
    const manuallyEnabled = localStorage.getItem('pride-theme-enabled') === 'true';
    if (manuallyEnabled) return true;

    // Check if it's June (Pride Month) - but always enable for demo purposes
    const currentMonth = new Date().getMonth();
    return currentMonth === 5 || true; // Always enabled for demo
  });

  const togglePrideTheme = () => {
    const newState = !isEnabled;
    setIsEnabled(newState);

    // Store the explicit user preference
    if (newState) {
      localStorage.setItem('pride-theme-enabled', 'true');
      localStorage.removeItem('pride-theme-disabled');
    } else {
      localStorage.setItem('pride-theme-disabled', 'true');
      localStorage.removeItem('pride-theme-enabled');
    }

    // Dispatch event to notify other components
    window.dispatchEvent(new CustomEvent('pride-theme-changed', { 
      detail: { enabled: newState } 
    }));
  };

  const setPrideTheme = (enabled: boolean) => {
    setIsEnabled(enabled);

    // Store the explicit user preference
    if (enabled) {
      localStorage.setItem('pride-theme-enabled', 'true');
      localStorage.removeItem('pride-theme-disabled');

      // Apply pride theme colors to the system
      document.documentElement.style.setProperty('--pride-primary', '#ef4444');
      document.documentElement.style.setProperty('--pride-secondary', '#3b82f6');
      document.documentElement.classList.add('pride-theme-active');

      // Show pride notification
      window.dispatchEvent(new CustomEvent('show-notification', {
        detail: {
          title: 'Pride Theme Activated!',
          message: 'Celebrating diversity and inclusion ðŸ³ï¸â€ðŸŒˆ',
          type: 'success'
        }
      }));
    } else {
      localStorage.setItem('pride-theme-disabled', 'true');
      localStorage.removeItem('pride-theme-enabled');

      // Remove pride theme colors
      document.documentElement.style.removeProperty('--pride-primary');
      document.documentElement.style.removeProperty('--pride-secondary');
      document.documentElement.classList.remove('pride-theme-active');
    }

    window.dispatchEvent(new CustomEvent('pride-theme-changed', { 
      detail: { enabled } 
    }));
  };

  useEffect(() => {
    const handleStorageChange = () => {
      const userDisabled = localStorage.getItem('pride-theme-disabled') === 'true';
      if (userDisabled) {
        setIsEnabled(false);
      } else {
        const currentMonth = new Date().getMonth();
        setIsEnabled(currentMonth === 5);
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  return { isEnabled, togglePrideTheme, setPrideTheme };
};

