import { FC, useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { 
  AlertTriangle, 
  Wrench, 
  HardDrive, 
  Download,
  RefreshCw,
  Terminal,
  Shield,
  Zap
} from 'lucide-react';

export const WebOSMakerRecovery: FC = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [progress, setProgress] = useState(0);
  const [isRecovering, setIsRecovering] = useState(false);

  const recoverySteps = [
    'Initializing WebOS Maker Recovery Environment',
    'Scanning for corrupted system files',
    'Attempting automatic repair',
    'Recovery process failed - manual intervention required',
    'Contact WebOS Support for advanced recovery options'
  ];

  useEffect(() => {
    if (isRecovering) {
      const interval = setInterval(() => {
        setProgress(prev => {
          if (prev >= 100) {
            clearInterval(interval);
            setCurrentStep(prev => Math.min(prev + 1, recoverySteps.length - 1));
            return 0;
          }
          return prev + 2;
        });
      }, 100);

      return () => clearInterval(interval);
    }
  }, [isRecovering, currentStep]);

  const startRecovery = () => {
    setIsRecovering(true);
    setCurrentStep(0);
    setProgress(0);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-red-900 via-red-800 to-black text-white flex flex-col items-center justify-center p-8">
      <div className="max-w-2xl w-full text-center space-y-8">
        {/* Header */}
        <div className="space-y-4">
          <div className="flex items-center justify-center space-x-4">
            <Wrench className="w-16 h-16 text-red-400" />
            <AlertTriangle className="w-20 h-20 text-yellow-400 animate-pulse" />
            <Wrench className="w-16 h-16 text-red-400" />
          </div>
          <h1 className="text-5xl font-bold">WebOS Maker Recovery</h1>
          <p className="text-xl text-red-200">Critical System Failure Detected</p>
        </div>

        {/* Error Information */}
        <div className="bg-red-800/50 border border-red-600 rounded-lg p-6 text-left">
          <h2 className="text-2xl font-bold mb-4 flex items-center">
            <Shield className="w-6 h-6 mr-2" />
            System WOS Corruption Detected
          </h2>
          <div className="space-y-2 text-red-100">
            <p>â€¢ Critical system files have been deleted or corrupted</p>
            <p>â€¢ Windows manager service has stopped responding</p>
            <p>â€¢ Desktop environment cannot be loaded</p>
            <p>â€¢ System integrity has been compromised</p>
          </div>
        </div>

        {/* Recovery Status */}
        <div className="bg-black/50 border border-gray-600 rounded-lg p-6">
          <h3 className="text-xl font-bold mb-4 flex items-center">
            <HardDrive className="w-5 h-5 mr-2" />
            Recovery Status
          </h3>

          {!isRecovering ? (
            <div className="space-y-4">
              <p className="text-gray-300">
                WebOS Maker will attempt to repair your system automatically.
                This process may take several minutes.
              </p>
              <Button 
                onClick={startRecovery}
                className="w-full bg-blue-600 hover:bg-blue-700"
                size="lg"
              >
                <RefreshCw className="w-5 h-5 mr-2" />
                Start System Recovery
              </Button>
            </div>
          ) : (
            <div className="space-y-4">
              <div className="text-left">
                <p className="font-medium mb-2">{recoverySteps[currentStep]}</p>
                <Progress value={progress} className="w-full" />
                <p className="text-sm text-gray-400 mt-1">{progress}% complete</p>
              </div>

              {currentStep >= 3 && (
                <div className="bg-red-900/50 border border-red-600 rounded p-4 text-left">
                  <h4 className="font-bold text-red-200 mb-2">Recovery Failed</h4>
                  <p className="text-red-100 text-sm">
                    Automatic recovery could not restore System WOS. 
                    The damage is too severe for automated repair.
                  </p>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Manual Recovery Options */}
        {currentStep >= 4 && (
          <div className="bg-gray-900/50 border border-gray-600 rounded-lg p-6 text-left">
            <h3 className="text-xl font-bold mb-4 flex items-center">
              <Terminal className="w-5 h-5 mr-2" />
              Manual Recovery Options
            </h3>
            <div className="space-y-3 text-gray-300">
              <p>â€¢ <strong>Complete System Reinstall:</strong> This will erase all data and restore WebOS to factory settings</p>
              <p>â€¢ <strong>Professional Recovery Service:</strong> Contact WebOS Support for advanced data recovery</p>
              <p>â€¢ <strong>System Backup Restore:</strong> Restore from a previous system backup (if available)</p>
            </div>

            <div className="mt-6 space-y-3">
              <Button variant="destructive" className="w-full" size="lg">
                <Download className="w-5 h-5 mr-2" />
                Download WebOS Recovery USB Tool
              </Button>
              <Button variant="outline" className="w-full" size="lg">
                <Zap className="w-5 h-5 mr-2" />
                Contact WebOS Support
              </Button>
            </div>
          </div>
        )}

        {/* Warning */}
        <div className="bg-yellow-900/30 border border-yellow-600 rounded-lg p-4">
          <p className="text-yellow-200 text-sm">
            <strong>Warning:</strong> Do not power off your computer during recovery. 
            This may cause permanent data loss and hardware damage.
          </p>
        </div>

        {/* Footer */}
        <div className="text-gray-400 text-sm space-y-1">
          <p>WebOS Maker Recovery Environment v3.0</p>
          <p>Build 22000.1.2024.0115.recovery</p>
          <p>If you can see this message, your hardware is functioning correctly.</p>
        </div>
      </div>
    </div>
  );
};

client/src/components/WebOSMascot.tsx.content:
import { FC, useState, useRef, useEffect } from 'react';
import { motion, useAnimation } from 'framer-motion';

interface WebOSMascotProps {
  isVisible?: boolean;
}

export const WebOSMascot: FC<WebOSMascotProps> = ({ isVisible = true }) => {
  const [position, setPosition] = useState({ x: 10, y: 10 });
  const [isDragging, setIsDragging] = useState(false);
  const [clickCount, setClickCount] = useState(0);
  const [isAngry, setIsAngry] = useState(false);
  const [showGun, setShowGun] = useState(false);
  const controls = useAnimation();
  const lastClickTime = useRef(0);

  useEffect(() => {
    // Spinning animation when not being dragged
    if (!isDragging && isVisible) {
      controls.start({
        rotate: 360,
        transition: {
          duration: 4,
          ease: "linear",
          repeat: Infinity
        }
      });
    } else {
      controls.stop();
    }
  }, [isDragging, isVisible, controls]);

  const handleClick = () => {
    const now = Date.now();

    // Reset click count if more than 3 seconds between clicks
    if (now - lastClickTime.current > 3000) {
      setClickCount(1);
    } else {
      setClickCount(prev => prev + 1);
    }

    lastClickTime.current = now;

    // Show gun after 10 rapid clicks
    if (clickCount >= 9) {
      setIsAngry(true);
      setShowGun(true);

      // Hide gun after 3 seconds
      setTimeout(() => {
        setShowGun(false);
        setIsAngry(false);
        setClickCount(0);
      }, 3000);
    } else if (clickCount >= 5) {
      setIsAngry(true);
      // Shake animation when getting annoyed
      controls.start({
        x: [0, -5, 5, -5, 5, 0],
        transition: { duration: 0.5 }
      });
    }
  };

  const handleDragStart = () => {
    setIsDragging(true);
    controls.stop();
  };

  const handleDragEnd = (event: any, info: any) => {
    setIsDragging(false);
    const newX = Math.max(0, Math.min(100, position.x + (info.offset.x / window.innerWidth) * 100));
    const newY = Math.max(0, Math.min(100, position.y + (info.offset.y / window.innerHeight) * 100));
    setPosition({ x: newX, y: newY });
  };

  if (!isVisible) return null;

  return (
    <motion.div
      className="fixed z-50 cursor-pointer select-none"
      style={{
        left: `${position.x}%`,
        top: `${position.y}%`,
        transform: 'translate(-50%, -50%)'
      }}
      animate={controls}
      drag
      dragMomentum={false}
      onDragStart={handleDragStart}
      onDragEnd={handleDragEnd}
      onClick={handleClick}
      whileHover={{ scale: 1.1 }}
      whileTap={{ scale: 0.95 }}
    >
      <div className="relative">
        {/* Main mascot body */}
        <div 
          className={`w-16 h-16 rounded-full border-4 border-blue-400 bg-gradient-to-br from-blue-500 to-purple-600 shadow-lg transition-all duration-300 ${
            isAngry ? 'border-red-400 from-red-500 to-orange-600' : ''
          }`}
        >
          {/* Eyes */}
          <div className="flex justify-center items-center h-full">
            <div className="flex space-x-2">
              <div 
                className={`w-2 h-2 rounded-full transition-all duration-300 ${
                  isAngry ? 'bg-red-300' : 'bg-white'
                }`} 
              />
              <div 
                className={`w-2 h-2 rounded-full transition-all duration-300 ${
                  isAngry ? 'bg-red-300' : 'bg-white'
                }`} 
              />
            </div>
          </div>

          {/* Mouth */}
          <div className="absolute bottom-2 left-1/2 transform -translate-x-1/2">
            {isAngry ? (
              <div className="w-3 h-1 bg-red-300 rounded-full transform rotate-180" />
            ) : (
              <div className="w-3 h-1 bg-white rounded-full" />
            )}
          </div>
        </div>

        {/* WebOS logo overlay */}
        <div className="absolute inset-0 flex items-center justify-center">
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" className="opacity-70">
            <path
              d="M12 2L2 7L12 12L22 7L12 2Z"
              stroke="white"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
            <path
              d="M2 17L12 22L22 17"
              stroke="white"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
            <path
              d="M2 12L12 17L22 12"
              stroke="white"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            />
          </svg>
        </div>

        {/* Fake gun when angry */}
        {showGun && (
          <motion.div
            className="absolute -right-8 top-1/2 transform -translate-y-1/2"
            initial={{ opacity: 0, scale: 0 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0 }}
          >
            <div className="w-6 h-3 bg-gray-700 rounded-sm relative">
              <div className="w-2 h-1 bg-gray-800 rounded-full absolute right-0 top-1/2 transform translate-x-full -translate-y-1/2" />
              <div className="w-1 h-4 bg-gray-600 absolute -bottom-1 left-1" />
            </div>
          </motion.div>
        )}

        {/* Click counter display when getting annoyed */}
        {clickCount >= 3 && clickCount < 10 && (
          <motion.div
            className="absolute -top-8 left-1/2 transform -translate-x-1/2 bg-red-500 text-white text-xs px-2 py-1 rounded"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
          >
            Stop it! ({clickCount}/10)
          </motion.div>
        )}

        {/* Angry message */}
        {showGun && (
          <motion.div
            className="absolute -top-12 left-1/2 transform -translate-x-1/2 bg-red-600 text-white text-xs px-3 py-2 rounded whitespace-nowrap"
            initial={{ opacity: 0, scale: 0 }}
            animate={{ opacity: 1, scale: 1 }}
          >
            That's enough! ðŸ”«
          </motion.div>
        )}

        {/* Spinning trail effect */}
        {!isDragging && (
          <div className="absolute inset-0 rounded-full border-2 border-blue-300 opacity-30 animate-ping" />
        )}
      </div>
    </motion.div>
  );
};

