import { FC, useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Checkbox } from '@/components/ui/checkbox';

interface WelcomeDialogProps {
  isOpen: boolean;
  onComplete: (userInfo: UserSetupInfo) => void;
}

interface UserSetupInfo {
  username: string;
  theme: string;
  wallpaper: string;
  showTutorial: boolean;
}

export const WelcomeDialog: FC<WelcomeDialogProps> = ({ isOpen, onComplete }) => {
  const [step, setStep] = useState(0);
  const [userInfo, setUserInfo] = useState<UserSetupInfo>({
    username: 'user',
    theme: 'dark',
    wallpaper: 'default',
    showTutorial: true
  });

  const steps = [
    {
      title: 'Welcome to WebOS!',
      content: (
        <div className="space-y-4">
          <div className="text-center">
            <div className="text-6xl mb-4">ðŸŽ‰</div>
            <p className="text-lg text-muted-foreground">
              Installation completed successfully! WebOS is now ready to use.
            </p>
            <p className="text-sm text-muted-foreground mt-2">
              Let's set up your system preferences to get you started.
            </p>
          </div>
        </div>
      )
    },
    {
      title: 'User Setup',
      content: (
        <div className="space-y-4">
          <div>
            <Label htmlFor="username">Choose a username</Label>
            <Input
              id="username"
              value={userInfo.username}
              onChange={(e) => setUserInfo(prev => ({ ...prev, username: e.target.value }))}
              placeholder="Enter your username"
              className="mt-1"
            />
            <p className="text-xs text-muted-foreground mt-1">
              This will be displayed in the terminal and system
            </p>
          </div>
        </div>
      )
    },
    {
      title: 'Appearance',
      content: (
        <div className="space-y-4">
          <div>
            <Label htmlFor="theme">Theme</Label>
            <Select 
              value={userInfo.theme} 
              onValueChange={(value) => setUserInfo(prev => ({ ...prev, theme: value }))}
            >
              <SelectTrigger className="mt-1">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="dark">Dark (Recommended)</SelectItem>
                <SelectItem value="light">Light</SelectItem>
                <SelectItem value="auto">Auto (System)</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div>
            <Label htmlFor="wallpaper">Wallpaper</Label>
            <Select 
              value={userInfo.wallpaper} 
              onValueChange={(value) => setUserInfo(prev => ({ ...prev, wallpaper: value }))}
            >
              <SelectTrigger className="mt-1">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="default">Arch Dark</SelectItem>
                <SelectItem value="terminal">Terminal Green</SelectItem>
                <SelectItem value="code">Code Blue</SelectItem>
                <SelectItem value="matrix">Matrix Black</SelectItem>
                <SelectItem value="cyber">Cyber Purple</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>
      )
    },
    {
      title: 'Getting Started',
      content: (
        <div className="space-y-4">
          <div className="flex items-center space-x-2">
            <Checkbox
              id="tutorial"
              checked={userInfo.showTutorial}
              onCheckedChange={(checked) => setUserInfo(prev => ({ ...prev, showTutorial: !!checked }))}
            />
            <Label htmlFor="tutorial">Show tutorial on first launch</Label>
          </div>

          <div className="bg-muted p-4 rounded-lg">
            <h4 className="font-medium mb-2">Quick Tips:</h4>
            <ul className="text-sm space-y-1 text-muted-foreground">
              <li>â€¢ Double-click desktop icons to open applications</li>
              <li>â€¢ Use the start menu for quick access to all apps</li>
              <li>â€¢ Terminal supports many Linux commands</li>
              <li>â€¢ Package manager lets you install new software</li>
              <li>â€¢ Settings app allows customization</li>
            </ul>
          </div>
        </div>
      )
    }
  ];

  const handleNext = () => {
    if (step < steps.length - 1) {
      setStep(step + 1);
    } else {
      handleComplete();
    }
  };

  const handlePrevious = () => {
    if (step > 0) {
      setStep(step - 1);
    }
  };

  const handleComplete = () => {
    localStorage.setItem('webos-user-setup', JSON.stringify(userInfo));
    localStorage.setItem('webos-setup-complete', 'true');
    onComplete(userInfo);
  };

  return (
    <Dialog open={isOpen}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center justify-between">
            {steps[step].title}
            <span className="text-sm text-muted-foreground">
              {step + 1} of {steps.length}
            </span>
          </DialogTitle>
        </DialogHeader>

        <div className="py-4">
          {steps[step].content}
        </div>

        <div className="flex justify-between">
          <Button
            variant="outline"
            onClick={handlePrevious}
            disabled={step === 0}
          >
            Previous
          </Button>

          <Button onClick={handleNext}>
            {step === steps.length - 1 ? 'Complete Setup' : 'Next'}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
};

client/src/components/WorkspaceManager.t.content:
import { FC, useState, useEffect } from 'react';
import { WindowState } from '@/types/os';

interface WorkspaceManagerProps {
  windows: WindowState[];
  activeWorkspace: number;
  onWorkspaceChange: (workspace: number) => void;
}

export const WorkspaceManager: FC<WorkspaceManagerProps> = ({
  windows,
  activeWorkspace,
  onWorkspaceChange
}) => {
  const [workspaces] = useState([1, 2, 3, 4, 5]);

  const getWorkspaceWindows = (workspace: number) => {
    return windows.filter(w => w.workspace === workspace && !w.minimized);
  };

  const handleWorkspaceClick = (workspace: number) => {
    onWorkspaceChange(workspace);
  };

  // Keyboard shortcuts for workspace switching
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.altKey && e.key >= '1' && e.key <= '5') {
        e.preventDefault();
        const workspace = parseInt(e.key);
        onWorkspaceChange(workspace);
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [onWorkspaceChange]);

  return (
    <div className="fixed bottom-16 left-1/2 transform -translate-x-1/2 z-50">
      <div className="bg-black/80 backdrop-blur-md rounded-lg px-4 py-2 border border-primary/30">
        <div className="flex space-x-2">
          {workspaces.map((workspace) => {
            const windowCount = getWorkspaceWindows(workspace).length;
            const isActive = workspace === activeWorkspace;

            return (
              <button
                key={workspace}
                onClick={() => handleWorkspaceClick(workspace)}
                className={`
                  relative w-12 h-8 rounded-md transition-all duration-200 
                  ${isActive 
                    ? 'bg-primary text-primary-foreground' 
                    : 'bg-muted/50 hover:bg-muted text-muted-foreground hover:text-foreground'
                  }
                `}
              >
                <span className="text-xs font-mono">{workspace}</span>
                {windowCount > 0 && (
                  <div className="absolute -top-1 -right-1 w-3 h-3 bg-cyan-400 rounded-full text-[8px] flex items-center justify-center text-black">
                    {windowCount}
                  </div>
                )}
              </button>
            );
          })}
        </div>
        <div className="text-xs text-center text-muted-foreground mt-1 font-mono">
          Alt+{activeWorkspace}
        </div>
      </div>
    </div>
  );
};

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

