const gameModes: GameMode[] = [
  {
    id: 'tdm',
    name: 'Team Deathmatch',
    description: 'Classic team vs team combat',
    maxPlayers: 12,
    mapRotation: ['Nuketown', 'Crash', 'Shipment', 'Rust'],
    duration: 600,
    featured: true
  },
  {
