        url: 'https://releases.ubuntu.com/22.04.3/ubuntu-22.04.3-desktop-amd64.iso',
        size: 4896235520, // ~4.6GB
        downloaded: 2147483648, // ~2GB
        status: 'downloading',
        speed: 1048576, // 1MB/s
        timeRemaining: 2550, // seconds
        type: 'archive',
        createdAt: new Date(Date.now() - 30 * 60 * 1000) // 30 min ago
      },
      {
        id: '2',
        filename: 'nodejs-20.10.0-linux-x64.tar.xz',
        url: 'https://nodejs.org/dist/v20.10.0/node-v20.10.0-linux-x64.tar.xz',
        size: 52428800, // ~50MB
        downloaded: 52428800,
        status: 'completed',
        speed: 0,
        timeRemaining: 0,
        type: 'archive',
        createdAt: new Date(Date.now() - 2 * 60 * 60 * 1000) // 2 hours ago
      },
      {
        id: '3',
        filename: 'sample-presentation.pdf',
        url: 'https://example.com/sample-presentation.pdf',
        size: 2097152, // 2MB
        downloaded: 1048576, // 1MB
        status: 'paused',
        speed: 0,
        timeRemaining: 0,
        type: 'document',
        createdAt: new Date(Date.now() - 10 * 60 * 1000) // 10 min ago
      }
    ];

    setDownloads(sampleDownloads);
  }, []);

  const getFileIcon = (type: string) => {
    switch (type) {
      case 'document': return <FileText className="w-5 h-5 text-blue-500" />;
      case 'image': return <Image className="w-5 h-5 text-green-500" />;
      case 'video': return <Video className="w-5 h-5 text-purple-500" />;
      case 'audio': return <Music className="w-5 h-5 text-orange-500" />;
      case 'archive': return <Archive className="w-5 h-5 text-yellow-500" />;
      case 'code': return <Code className="w-5 h-5 text-cyan-500" />;
      default: return <FileText className="w-5 h-5 text-gray-500" />;
    }
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const formatSpeed = (bytesPerSecond: number) => {
    return formatFileSize(bytesPerSecond) + '/s';
  };

  const formatTimeRemaining = (seconds: number) => {
    if (seconds === 0) return '--';
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;

    if (hours > 0) {
      return `${hours}h ${minutes}m ${secs}s`;
    } else if (minutes > 0) {
      return `${minutes}m ${secs}s`;
    } else {
      return `${secs}s`;
    }
  };

  const getFileTypeFromUrl = (url: string): DownloadItem['type'] => {
    const extension = url.split('.').pop()?.toLowerCase() || '';

    if (['pdf', 'doc', 'docx', 'txt', 'rtf'].includes(extension)) return 'document';
    if (['jpg', 'jpeg', 'png', 'gif', 'bmp', 'svg', 'webp'].includes(extension)) return 'image';
    if (['mp4', 'avi', 'mkv', 'webm', 'mov', 'wmv'].includes(extension)) return 'video';
    if (['mp3', 'wav', 'flac', 'ogg', 'aac'].includes(extension)) return 'audio';
    if (['zip', 'rar', 'tar', 'gz', 'xz', '7z', 'iso'].includes(extension)) return 'archive';
    if (['js', 'ts', 'py', 'cpp', 'c', 'java', 'html', 'css'].includes(extension)) return 'code';

    return 'other';
  };

  const addDownload = () => {
    if (!downloadUrl.trim()) return;

    playSounds.buttonClick();

    const filename = downloadUrl.split('/').pop() || 'unknown_file';
    const newDownload: DownloadItem = {
      id: Date.now().toString(),
      filename,
      url: downloadUrl,
      size: Math.floor(Math.random() * 100000000) + 1000000, // Random size for demo
      downloaded: 0,
      status: 'queued',
      speed: 0,
      timeRemaining: 0,
      type: getFileTypeFromUrl(downloadUrl),
      createdAt: new Date()
    };

    setDownloads(prev => [newDownload, ...prev]);
    setDownloadUrl('');

    // Simulate download start
    setTimeout(() => {
      setDownloads(prev => prev.map(d => 
        d.id === newDownload.id ? { ...d, status: 'downloading' as const } : d
      ));
    }, 1000);
  };

  const pauseDownload = (id: string) => {
    playSounds.buttonClick();
    setDownloads(prev => prev.map(d => 
      d.id === id ? { ...d, status: 'paused' as const, speed: 0 } : d
    ));
  };

  const resumeDownload = (id: string) => {
    playSounds.buttonClick();
    setDownloads(prev => prev.map(d => 
      d.id === id ? { ...d, status: 'downloading' as const } : d
    ));
  };

  const cancelDownload = (id: string) => {
    playSounds.buttonClick();
    setDownloads(prev => prev.filter(d => d.id !== id));
  };

  const openDownloadFolder = () => {
    playSounds.folderOpen();
    // Simulate opening file manager to downloads folder
    const event = new CustomEvent('open-app', { detail: { app: 'file-manager', path: downloadPath } });
    window.dispatchEvent(event);
  };

  const activeDownloads = downloads.filter(d => d.status === 'downloading' || d.status === 'queued');
  const completedDownloads = downloads.filter(d => d.status === 'completed');
  const pausedDownloads = downloads.filter(d => d.status === 'paused' || d.status === 'error');

  // Simulate download progress
  useEffect(() => {
    const interval = setInterval(() => {
      setDownloads(prev => prev.map(download => {
        if (download.status === 'downloading' && download.downloaded < download.size) {
          const increment = Math.floor(Math.random() * 1048576) + 102400; // Random progress
          const newDownloaded = Math.min(download.downloaded + increment, download.size);
          const isComplete = newDownloaded >= download.size;

          return {
            ...download,
            downloaded: newDownloaded,
            status: isComplete ? 'completed' as const : download.status,
            speed: isComplete ? 0 : Math.floor(Math.random() * 2097152) + 524288, // Random speed
            timeRemaining: isComplete ? 0 : Math.floor((download.size - newDownloaded) / 1048576)
          };
        }
        return download;
      }));
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="h-full flex flex-col bg-background">
      {/* Header */}
      <div className="border-b p-4">
        <h2 className="text-xl font-semibold mb-4">Download Manager</h2>

        {/* System Info */}
        <div className="grid grid-cols-2 gap-4 mb-4 text-sm">
          <div>
            <span className="font-medium">OS:</span> {systemInfo.os}
          </div>
          <div>
            <span className="font-medium">Architecture:</span> {systemInfo.architecture}
          </div>
          <div>
            <span className="font-medium">Kernel:</span> {systemInfo.kernel}
          </div>
          <div>
            <span className="font-medium">Desktop:</span> {systemInfo.desktop}
          </div>
        </div>

        {/* Add Download */}
        <div className="space-y-4">
          <div className="grid grid-cols-4 gap-2">
            <div className="col-span-2">
              <Label htmlFor="url">Download URL</Label>
              <Input
                id="url"
                value={downloadUrl}
                onChange={(e) => setDownloadUrl(e.target.value)}
                placeholder="https://example.com/file.zip"
                onKeyPress={(e) => e.key === 'Enter' && addDownload()}
              />
            </div>
            <div>
              <Label htmlFor="path">Download Path</Label>
              <Select value={downloadPath} onValueChange={setDownloadPath}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="/home/html/Downloads">~/Downloads</SelectItem>
                  <SelectItem value="/home/html/Desktop">~/Desktop</SelectItem>
                  <SelectItem value="/home/html/Documents">~/Documents</SelectItem>
                  <SelectItem value="/tmp">Temporary</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div className="flex items-end gap-2">
              <Button onClick={addDownload} className="flex-1">
                <Download className="w-4 h-4 mr-2" />
                Download
              </Button>
            </div>
          </div>
        </div>
      </div>

      {/* Downloads List */}
      <div className="flex-1 p-4">
        <Tabs value={activeTab} onValueChange={setActiveTab}>
          <div className="flex items-center justify-between mb-4">
            <TabsList>
              <TabsTrigger value="active">
                Active ({activeDownloads.length})
              </TabsTrigger>
              <TabsTrigger value="completed">
                Completed ({completedDownloads.length})
              </TabsTrigger>
              <TabsTrigger value="paused">
                Paused ({pausedDownloads.length})
              </TabsTrigger>
            </TabsList>

            <Button variant="outline" onClick={openDownloadFolder}>
              <Folder className="w-4 h-4 mr-2" />
              Open Folder
            </Button>
          </div>

          <TabsContent value="active" className="space-y-2">
            {activeDownloads.map(download => (
              <Card key={download.id}>
                <CardContent className="p-4">
                  <div className="flex items-center gap-3">
                    {getFileIcon(download.type)}
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center justify-between mb-2">
                        <h4 className="font-medium truncate">{download.filename}</h4>
                        <div className="flex items-center gap-2">
                          {download.status === 'downloading' ? (
                            <Button variant="outline" size="sm" onClick={() => pauseDownload(download.id)}>
                              <Pause className="w-3 h-3" />
                            </Button>
                          ) : (
                            <Button variant="outline" size="sm" onClick={() => resumeDownload(download.id)}>
                              <Play className="w-3 h-3" />
                            </Button>
                          )}
                          <Button variant="outline" size="sm" onClick={() => cancelDownload(download.id)}>
                            <X className="w-3 h-3" />
                          </Button>
                        </div>
                      </div>

                      <div className="space-y-2">
                        <Progress 
                          value={(download.downloaded / download.size) * 100} 
                          className="h-2"
                        />
                        <div className="flex justify-between text-xs text-muted-foreground">
                          <span>
                            {formatFileSize(download.downloaded)} / {formatFileSize(download.size)}
                          </span>
                          <span>
                            {download.status === 'downloading' && `${formatSpeed(download.speed)} â€¢ ${formatTimeRemaining(download.timeRemaining)}`}
                            {download.status === 'queued' && 'Queued'}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
            {activeDownloads.length === 0 && (
              <div className="text-center py-8 text-muted-foreground">
                No active downloads
              </div>
            )}
          </TabsContent>

          <TabsContent value="completed" className="space-y-2">
            {completedDownloads.map(download => (
              <Card key={download.id}>
                <CardContent className="p-4">
                  <div className="flex items-center gap-3">
                    {getFileIcon(download.type)}
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center justify-between">
                        <h4 className="font-medium truncate">{download.filename}</h4>
                        <div className="text-xs text-muted-foreground">
                          {formatFileSize(download.size)}
                        </div>
                      </div>
                      <div className="text-xs text-muted-foreground mt-1">
                        Completed {download.createdAt.toLocaleString()}
                      </div>
                    </div>
                    <Button variant="outline" size="sm" onClick={() => cancelDownload(download.id)}>
                      <X className="w-3 h-3" />
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
            {completedDownloads.length === 0 && (
              <div className="text-center py-8 text-muted-foreground">
                No completed downloads
              </div>
            )}
          </TabsContent>

          <TabsContent value="paused" className="space-y-2">
            {pausedDownloads.map(download => (
              <Card key={download.id}>
                <CardContent className="p-4">
                  <div className="flex items-center gap-3">
                    {getFileIcon(download.type)}
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center justify-between mb-2">
                        <h4 className="font-medium truncate">{download.filename}</h4>
                        <div className="flex items-center gap-2">
                          <Button variant="outline" size="sm" onClick={() => resumeDownload(download.id)}>
                            <Play className="w-3 h-3" />
                          </Button>
                          <Button variant="outline" size="sm" onClick={() => cancelDownload(download.id)}>
                            <X className="w-3 h-3" />
                          </Button>
                        </div>
                      </div>

                      <div className="space-y-2">
                        <Progress 
                          value={(download.downloaded / download.size) * 100} 
                          className="h-2"
                        />
                        <div className="flex justify-between text-xs text-muted-foreground">
                          <span>
                            {formatFileSize(download.downloaded)} / {formatFileSize(download.size)}
                          </span>
                          <span className="text-orange-500">
                            {download.status === 'paused' ? 'Paused' : 'Error'}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
            {pausedDownloads.length === 0 && (
              <div className="text-center py-8 text-muted-foreground">
                No paused downloads
              </div>
            )}
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
};

client/src/components/apps/EmailClient.t.content:
import { FC, useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardHeader, CardContent, CardTitle } from '@/components/ui/card';
import { Mail, Send, Inbox, Edit, Search } from 'lucide-react';

interface Email {
  id: string;
  from: string;
  to: string;
  subject: string;
  body: string;
  timestamp: Date;
  read: boolean;
}

export const EmailClient: FC = () => {
  const [emails, setEmails] = useState<Email[]>(() => {
    const stored = localStorage.getItem('email-messages');
    return stored ? JSON.parse(stored).map((e: any) => ({
      ...e,
      timestamp: new Date(e.timestamp)
    })) : [
      {
        id: '1',
        from: 'team@webos.dev',
        to: 'user@webos.local',
        subject: 'Welcome to WebOS Email',
        body: 'Thank you for installing the email client. This is a sample email to demonstrate functionality.',
        timestamp: new Date(),
        read: false
      }
    ];
  });

  const [selectedEmail, setSelectedEmail] = useState<Email | null>(null);
  const [showCompose, setShowCompose] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [composeData, setComposeData] = useState({
    to: '',
    subject: '',
    body: ''
  });

  const filteredEmails = emails.filter(email =>
    email.subject.toLowerCase().includes(searchTerm.toLowerCase()) ||
    email.from.toLowerCase().includes(searchTerm.toLowerCase()) ||
    email.body.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleEmailSelect = (email: Email) => {
    if (!email.read) {
      const updatedEmails = emails.map(e =>
        e.id === email.id ? { ...e, read: true } : e
      );
      setEmails(updatedEmails);
      localStorage.setItem('email-messages', JSON.stringify(updatedEmails));
    }
    setSelectedEmail(email);
    setShowCompose(false);
  };

  const handleSendEmail = () => {
    if (composeData.to && composeData.subject) {
      const newEmail: Email = {
        id: Date.now().toString(),
        from: 'user@webos.local',
        to: composeData.to,
        subject: composeData.subject,
        body: composeData.body,
        timestamp: new Date(),
        read: true
      };

      const updatedEmails = [newEmail, ...emails];
      setEmails(updatedEmails);
      localStorage.setItem('email-messages', JSON.stringify(updatedEmails));

      setComposeData({ to: '', subject: '', body: '' });
      setShowCompose(false);
      setSelectedEmail(newEmail);
    }
  };

  const formatDate = (date: Date) => {
    return date.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="h-full flex">
      {/* Sidebar */}
      <div className="w-1/3 border-r border-border bg-muted/30">
        <div className="p-4 border-b border-border">
          <div className="flex items-center gap-2 mb-4">
            <Mail className="w-5 h-5 text-primary" />
            <span className="font-semibold">Email</span>
          </div>

          <div className="relative mb-4">
            <Search className="w-4 h-4 absolute left-3 top-3 text-muted-foreground" />
            <Input
              placeholder="Search emails..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10"
            />
          </div>

          <Button 
            onClick={() => { setShowCompose(true); setSelectedEmail(null); }}
            className="w-full"
          >
            <Edit className="w-4 h-4 mr-2" />
            Compose
          </Button>
        </div>

        <div className="overflow-auto">
          {filteredEmails.map(email => (
            <div
              key={email.id}
              onClick={() => handleEmailSelect(email)}
              className={`p-4 border-b border-border cursor-pointer hover:bg-muted/50 ${
                selectedEmail?.id === email.id ? 'bg-primary/10' : ''
              } ${!email.read ? 'font-semibold' : ''}`}
            >
              <div className="flex justify-between items-start mb-1">
                <span className="text-sm font-medium truncate">{email.from}</span>
                <span className="text-xs text-muted-foreground">{formatDate(email.timestamp)}</span>
              </div>
              <div className="text-sm truncate mb-1">{email.subject}</div>
              <div className="text-xs text-muted-foreground truncate">{email.body}</div>
              {!email.read && (
                <div className="w-2 h-2 bg-primary rounded-full mt-2"></div>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {showCompose ? (
          <div className="p-6">
            <h2 className="text-lg font-semibold mb-4">Compose Email</h2>
            <div className="space-y-4">
              <Input
                placeholder="To:"
                value={composeData.to}
                onChange={(e) => setComposeData({...composeData, to: e.target.value})}
              />
              <Input
                placeholder="Subject:"
                value={composeData.subject}
                onChange={(e) => setComposeData({...composeData, subject: e.target.value})}
              />
              <Textarea
                placeholder="Message:"
                value={composeData.body}
                onChange={(e) => setComposeData({...composeData, body: e.target.value})}
                className="min-h-64"
              />
              <div className="flex gap-2">
                <Button onClick={handleSendEmail}>
                  <Send className="w-4 h-4 mr-2" />
                  Send
                </Button>
                <Button variant="outline" onClick={() => setShowCompose(false)}>
                  Cancel
                </Button>
              </div>
            </div>
          </div>
        ) : selectedEmail ? (
          <div className="p-6">
            <div className="mb-4">
              <h2 className="text-lg font-semibold">{selectedEmail.subject}</h2>
              <div className="text-sm text-muted-foreground mt-1">
                From: {selectedEmail.from} | To: {selectedEmail.to}
              </div>
              <div className="text-sm text-muted-foreground">
                {formatDate(selectedEmail.timestamp)}
              </div>
            </div>
            <div className="prose prose-sm">
              {selectedEmail.body.split('\n').map((line, index) => (
                <p key={index}>{line}</p>
              ))}
            </div>
          </div>
        ) : (
          <div className="flex-1 flex items-center justify-center text-muted-foreground">
            <div className="text-center">
              <Inbox className="w-12 h-12 mx-auto mb-4 opacity-50" />
              <p>Select an email to read or compose a new message</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

client/src/components/apps/EnhancedTermi.content:
import { FC, useState, useRef, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { X, Terminal as TerminalIcon, Activity, Cpu, HardDrive, Wifi } from 'lucide-react';

interface TerminalLine {
  type: 'command' | 'output' | 'error' | 'success' | 'system';
  content: string;
  timestamp: Date;
}

export const EnhancedTerminal: FC = () => {
  const [lines, setLines] = useState<TerminalLine[]>([
    {
      type: 'system',
      content: 'WebOS Enhanced Terminal v5.0 - Real-time System Interface',
      timestamp: new Date()
    },
    {
      type: 'system',
      content: 'Type "help" for commands, "system-status" for real-time monitoring',
      timestamp: new Date()
    }
  ]);
  const [currentCommand, setCurrentCommand] = useState('');
  const [commandHistory, setCommandHistory] = useState<string[]>([]);
  const [historyIndex, setHistoryIndex] = useState(-1);
  const [isMonitoring, setIsMonitoring] = useState(false);
  const inputRef = useRef<HTMLInputElement>(null);
  const terminalRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (terminalRef.current) {
      terminalRef.current.scrollTop = terminalRef.current.scrollHeight;
    }
  }, [lines]);

  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isMonitoring) {
      interval = setInterval(() => {
        const timestamp = new Date();
        const cpu = Math.floor(Math.random() * 100);
        const memory = Math.floor(Math.random() * 100);
        const disk = 78;

        setLines(prev => [...prev, {
          type: 'system',
          content: `[${timestamp.toLocaleTimeString()}] CPU: ${cpu}% | Memory: ${memory}% | Disk: ${disk}% | Network: ${Math.floor(Math.random() * 50)}MB/s`,
          timestamp
        }]);
      }, 3000);
    }
    return () => {
      if (interval) clearInterval(interval);
    };
  }, [isMonitoring]);

  const executeCommand = async (cmd: string) => {
    const trimmedCmd = cmd.trim();
    if (!trimmedCmd) return;

    const commandLine: TerminalLine = {
      type: 'command',
      content: `user@webos:~$ ${trimmedCmd}`,
      timestamp: new Date()
    };

    let output = '';
    let outputType: 'output' | 'error' | 'success' = 'output';

    if (trimmedCmd === 'help') {
      output = `Enhanced WebOS Terminal Commands:
======================================
SYSTEM MONITORING:
  system-status         - Real-time system monitoring toggle
  process-monitor       - Show running processes
  network-monitor       - Network activity monitoring
  performance-test      - Run performance benchmarks
  disk-analyzer         - Analyze disk usage patterns

LIVE DATA SERVICES:
  check-apis           - Test external API connectivity
  weather [location]   - Get weather data (requires API key)
  crypto-prices        - Cryptocurrency market data
  stock-quote [symbol] - Stock market quotes
  news-feed [category] - Latest news headlines

ADVANCED SYSTEM:
  gpu-info             - Graphics card information
  thermal-status       - CPU/GPU temperature monitoring
  power-management     - Battery and power statistics
  security-scan        - System security audit
  backup-status        - Backup and recovery status

DEVELOPMENT TOOLS:
  docker-status        - Container management
  git-projects         - Git repository management
  database-connect     - Database connectivity
  api-endpoints        - Available API endpoints
  log-analyzer         - System log analysis

Type any command for detailed help or usage examples.`;
    } else if (trimmedCmd === 'system-status') {
      setIsMonitoring(!isMonitoring);
      output = isMonitoring ? 'Real-time monitoring stopped' : 'Real-time monitoring started';
      outputType = 'success';
    } else if (trimmedCmd === 'process-monitor') {
      output = `Process Monitor - Active Processes:
=====================================
PID    NAME              CPU%   MEM%   STATUS
1      systemd           0.1    0.2    Running
789    gnome-shell       8.5    12.3   Running
1024   firefox           15.2   25.8   Running
1156   webos-terminal    3.4    5.1    Running
2048   nodejs            12.8   18.6   Running
3072   docker-daemon     2.1    8.9    Running
4096   postgresql        1.8    15.2   Running
5120   nginx             0.5    2.3    Running
6144   redis-server      0.8    4.7    Running
7168   elasticsearch     5.3    22.1   Running

Total Processes: 247 | Running: 189 | Sleeping: 58`;
    } else if (trimmedCmd === 'network-monitor') {
      output = `Network Activity Monitor:
========================
Interface: enp0s3
  IP Address: 192.168.1.100/24
  Gateway: 192.168.1.1
  DNS Servers: 8.8.8.8, 1.1.1.1

Current Activity:
  Download: ${Math.floor(Math.random() * 100)}MB/s
  Upload: ${Math.floor(Math.random() * 50)}MB/s
  Packets In: ${Math.floor(Math.random() * 10000)}/sec
  Packets Out: ${Math.floor(Math.random() * 5000)}/sec

Active Connections: 45
Listening Ports: 22, 80, 443, 3000, 5432, 6379`;
    } else if (trimmedCmd === 'performance-test') {
      output = `System Performance Benchmark:
============================
CPU Performance Test... âš¡ 8,432 MIPS
Memory Bandwidth Test... ðŸ“Š 15.6 GB/s
Disk I/O Test... ðŸ’¾ 542 MB/s read, 387 MB/s write
Network Latency Test... ðŸŒ 12ms avg
Graphics Performance... ðŸŽ® 9,847 FPS

Overall Performance Score: 8.7/10
System Classification: High Performance Workstation`;
      outputType = 'success';
    } else if (trimmedCmd === 'disk-analyzer') {
      output = `Disk Usage Analysis:
==================
/ (root)          20GB  Used: 14.2GB (71%)  Available: 5.8GB
/home             50GB  Used: 32.1GB (64%)  Available: 17.9GB
/var              10GB  Used: 3.8GB (38%)   Available: 6.2GB
/tmp              2GB   Used: 0.3GB (15%)   Available: 1.7GB

Largest Directories:
/home/user/Documents    8.5GB
/var/log               2.1GB
/usr/share             4.3GB
/opt/applications      6.7GB

Disk Health: âœ“ Good
SSD Wear Level: 12%
Estimated Lifespan: 8.2 years`;
    } else if (trimmedCmd === 'check-apis') {
      // Test API connectivity
      try {
        const responses = await Promise.allSettled([
          fetch('/api/weather?location=test'),
          fetch('/api/crypto'),
          fetch('/api/news'),
          fetch('/api/stocks?symbol=AAPL')
        ]);

        output = `API Connectivity Status:
=======================
Weather API: ${responses[0].status === 'fulfilled' ? 'âœ“ Connected' : 'âœ— Authentication Error'}
Crypto API: ${responses[1].status === 'fulfilled' ? 'âœ“ Connected' : 'âœ— Authentication Error'}
News API: ${responses[2].status === 'fulfilled' ? 'âœ“ Connected' : 'âœ— Authentication Error'}
Stock API: ${responses[3].status === 'fulfilled' ? 'âœ“ Connected' : 'âœ— Authentication Error'}

Note: API errors typically indicate invalid or expired API keys.
Use valid credentials for live data access.`;
      } catch (error) {
        output = 'Error testing API connectivity';
        outputType = 'error';
      }
    } else if (trimmedCmd.startsWith('weather ')) {
      const location = trimmedCmd.split(' ').slice(1).join(' ') || 'London';
      output = `Weather Service Integration:
============================
Attempting to fetch weather data for: ${location}

Note: Live weather data requires valid OpenWeatherMap API key.
Configure API credentials for real-time weather information.

Weather service features:
â€¢ Current conditions and forecasts
â€¢ Temperature, humidity, wind data  
â€¢ 5-day weather predictions
â€¢ Severe weather alerts`;
    } else if (trimmedCmd === 'crypto-prices') {
      output = `Cryptocurrency Market Integration:
=================================
Connecting to CoinAPI for live market data...

Note: Live crypto data requires valid CoinAPI key.
Configure API credentials for real-time pricing.

Features available with API access:
â€¢ Real-time cryptocurrency prices
â€¢ Market capitalization data
â€¢ 24-hour trading volumes
â€¢ Price change percentages
â€¢ Historical data analysis`;
    } else if (trimmedCmd.startsWith('stock-quote ')) {
      const symbol = trimmedCmd.split(' ')[1] || 'AAPL';
      output = `Stock Market Integration:
========================
Fetching quote for: ${symbol.toUpperCase()}

Note: Live stock data requires valid Alpha Vantage API key.
Configure API credentials for real-time market data.

Features available with API access:
â€¢ Real-time stock quotes
â€¢ Intraday and historical data
â€¢ Company fundamentals
â€¢ Technical indicators
â€¢ Market analysis tools`;
    } else if (trimmedCmd.startsWith('news-feed ')) {
      const category = trimmedCmd.split(' ')[1] || 'technology';
      output = `News Feed Integration:
=====================
Fetching ${category} news headlines...

Note: Live news data requires valid NewsAPI key.
Configure API credentials for real-time news feeds.

Features available with API access:
â€¢ Breaking news headlines
â€¢ Category-specific feeds
â€¢ Source filtering
â€¢ Search functionality
â€¢ Article summaries`;
    } else if (trimmedCmd === 'gpu-info') {
      output = `Graphics Processing Unit Information:
=====================================
GPU Model: NVIDIA GeForce RTX 4080
Driver Version: 545.29.06
CUDA Version: 12.3
Memory: 16GB GDDR6X
Memory Clock: 1188 MHz
Core Clock: 2505 MHz
Temperature: ${35 + Math.floor(Math.random() * 25)}Â°C
Power Usage: ${150 + Math.floor(Math.random() * 170)}W
Performance State: P0 (Maximum Performance)

OpenGL Version: 4.6.0
Vulkan Support: 1.3.0
DirectX Support: 12 Ultimate`;
    } else if (trimmedCmd === 'thermal-status') {
      output = `Thermal Monitoring System:
=========================
CPU Temperature: ${45 + Math.floor(Math.random() * 25)}Â°C (Normal)
GPU Temperature: ${40 + Math.floor(Math.random() * 30)}Â°C (Normal)
Motherboard: ${35 + Math.floor(Math.random() * 15)}Â°C
Storage SSD: ${32 + Math.floor(Math.random() * 18)}Â°C

Fan Speeds:
CPU Cooler: ${800 + Math.floor(Math.random() * 800)} RPM
GPU Fans: ${1200 + Math.floor(Math.random() * 1000)} RPM
Case Fans: ${600 + Math.floor(Math.random() * 400)} RPM

Thermal Status: âœ“ All components within safe operating temperatures`;
    } else if (trimmedCmd === 'clear') {
      setLines([]);
      return;
    } else {
      output = `Command not found: ${trimmedCmd}
Type 'help' for available commands or 'check-apis' to test live data services.`;
      outputType = 'error';
    }

    const outputLine: TerminalLine = {
      type: outputType,
      content: output,
      timestamp: new Date()
    };

    setLines(prev => [...prev, commandLine, outputLine]);
    setCommandHistory(prev => [...prev, trimmedCmd]);
    setCurrentCommand('');
    setHistoryIndex(-1);
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      executeCommand(currentCommand);
    } else if (e.key === 'ArrowUp') {
      e.preventDefault();
      if (commandHistory.length > 0) {
        const newIndex = historyIndex === -1 ? commandHistory.length - 1 : Math.max(0, historyIndex - 1);
        setHistoryIndex(newIndex);
        setCurrentCommand(commandHistory[newIndex]);
      }
    } else if (e.key === 'ArrowDown') {
      e.preventDefault();
      if (historyIndex !== -1) {
        const newIndex = historyIndex + 1;
        if (newIndex >= commandHistory.length) {
          setHistoryIndex(-1);
          setCurrentCommand('');
        } else {
          setHistoryIndex(newIndex);
          setCurrentCommand(commandHistory[newIndex]);
        }
      }
    }
  };

  return (
    <div className="bg-black rounded-lg shadow-2xl overflow-hidden border border-green-500/20 w-full h-full">
      <div className="bg-gray-900 px-4 py-2 flex items-center justify-between border-b border-green-500/20">
        <div className="flex items-center space-x-2">
          <TerminalIcon className="w-4 h-4 text-green-400" />
          <span className="text-green-400 font-medium">Enhanced Terminal</span>
          {isMonitoring && (
            <div className="flex items-center space-x-1">
              <Activity className="w-3 h-3 text-yellow-400 animate-pulse" />
              <span className="text-yellow-400 text-xs">MONITORING</span>
            </div>
          )}
        </div>
        <div className="flex items-center space-x-2 text-xs text-gray-400">
          <Cpu className="w-3 h-3" />
          <span>{Math.floor(Math.random() * 100)}%</span>
          <HardDrive className="w-3 h-3" />
          <span>78%</span>
          <Wifi className="w-3 h-3" />
          <span>{Math.floor(Math.random() * 50)}MB/s</span>
        </div>
      </div>

      <div 
        ref={terminalRef}
        className="bg-black text-green-400 p-4 h-96 overflow-y-auto font-mono text-sm"
      >
        {lines.map((line, index) => (
          <div key={index} className="mb-1">
            <span className="text-gray-500 text-xs mr-2">
              {line.timestamp.toLocaleTimeString()}
            </span>
            <span className={
              line.type === 'command' ? 'text-yellow-400' :
              line.type === 'error' ? 'text-red-400' :
              line.type === 'success' ? 'text-green-400' :
              line.type === 'system' ? 'text-cyan-400' :
              'text-green-400'
            }>
              {line.content.split('\n').map((subLine, subIndex) => (
                <div key={subIndex}>{subLine}</div>
              ))}
            </span>
          </div>
        ))}

        <div className="flex items-center">
          <span className="text-yellow-400 mr-2">
            {new Date().toLocaleTimeString()} user@webos:~$
          </span>
          <input
            ref={inputRef}
            type="text"
            value={currentCommand}
            onChange={(e) => setCurrentCommand(e.target.value)}
            onKeyDown={handleKeyDown}
            className="bg-transparent text-green-400 outline-none flex-1 font-mono"
            autoFocus
          />
        </div>
      </div>
    </div>
  );
};

