import { FC, useState, useEffect } from 'react';
import { Wifi, WifiOff, Activity, Zap, Clock, AlertTriangle, CheckCircle, RotateCcw, Server, Globe } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { ConnectionStatus } from '@/components/ui/connection-status';
import { wsManager } from '@/lib/websocketManager';

interface NetworkMetrics {
  latency: number;
  jitter: number;
  packetLoss: number;
  bandwidth: number;
  connectionQuality: 'excellent' | 'good' | 'fair' | 'poor';
}

interface ConnectionLog {
  id: string;
  timestamp: Date;
  event: 'connected' | 'disconnected' | 'reconnecting' | 'error';
  message: string;
  duration?: number;
}

export const NetworkStatusMonitor: FC = () => {
  const [wsStatus, setWsStatus] = useState(wsManager.getStatus());
  const [connectionLogs, setConnectionLogs] = useState<ConnectionLog[]>([]);
  const [metrics, setMetrics] = useState<NetworkMetrics>({
    latency: 0,
    jitter: 0,
    packetLoss: 0,
    bandwidth: 0,
    connectionQuality: 'good'
  });
  const [isTestingConnection, setIsTestingConnection] = useState(false);
  const [lastConnectedTime, setLastConnectedTime] = useState<Date | null>(null);

  useEffect(() => {
    const unsubscribe = wsManager.onStatusChange((status) => {
      const previousStatus = wsStatus;
      setWsStatus(status);

      // Log connection events
      const logEntry: ConnectionLog = {
        id: Date.now().toString(),
        timestamp: new Date(),
        event: status.isConnected ? 'connected' : 
               status.isReconnecting ? 'reconnecting' : 'disconnected',
        message: getStatusMessage(status, previousStatus),
        duration: status.isConnected && lastConnectedTime ? 
                 Date.now() - lastConnectedTime.getTime() : undefined
      };

      setConnectionLogs(prev => [logEntry, ...prev.slice(0, 49)]);

      if (status.isConnected) {
        setLastConnectedTime(new Date());
        updateMetrics(status);
      }
    });

    // Start connection monitoring
    monitorConnection();

    return unsubscribe;
  }, []);

  const getStatusMessage = (current: any, previous: any) => {
    if (current.isConnected && !previous.isConnected) {
      return 'WebSocket connection established successfully';
    } else if (!current.isConnected && previous.isConnected) {
      return current.lastError || 'Connection lost';
    } else if (current.isReconnecting) {
      return `Reconnection attempt ${current.reconnectAttempts} of 10`;
    } else if (current.lastError) {
      return current.lastError;
    }
    return 'Connection status updated';
  };

  const updateMetrics = (status: any) => {
    setMetrics(prev => ({
      ...prev,
      latency: status.latency || prev.latency,
      connectionQuality: getConnectionQuality(status.latency || 0)
    }));
  };

  const getConnectionQuality = (latency: number): NetworkMetrics['connectionQuality'] => {
    if (latency < 50) return 'excellent';
    if (latency < 100) return 'good';
    if (latency < 200) return 'fair';
    return 'poor';
  };

  const monitorConnection = () => {
    const interval = setInterval(() => {
      if (wsStatus.isConnected) {
        // Simulate network metrics updates
        setMetrics(prev => ({
          ...prev,
          jitter: Math.random() * 10,
          packetLoss: Math.random() * 2,
          bandwidth: 50 + Math.random() * 50
        }));
      }
    }, 5000);

    return () => clearInterval(interval);
  };

  const testConnection = async () => {
    setIsTestingConnection(true);

    try {
      // Test connection with ping
      const startTime = Date.now();
      const success = wsManager.send({
        type: 'ping',
        timestamp: startTime
      });

      if (success) {
        const logEntry: ConnectionLog = {
          id: Date.now().toString(),
          timestamp: new Date(),
          event: 'connected',
          message: 'Connection test initiated'
        };
        setConnectionLogs(prev => [logEntry, ...prev.slice(0, 49)]);
      }
    } catch (error) {
      const logEntry: ConnectionLog = {
        id: Date.now().toString(),
        timestamp: new Date(),
        event: 'error',
        message: 'Connection test failed'
      };
      setConnectionLogs(prev => [logEntry, ...prev.slice(0, 49)]);
    } finally {
      setTimeout(() => setIsTestingConnection(false), 2000);
    }
  };

  const getQualityColor = (quality: string) => {
    switch (quality) {
      case 'excellent': return 'text-green-600';
      case 'good': return 'text-blue-600';
      case 'fair': return 'text-yellow-600';
      case 'poor': return 'text-red-600';
      default: return 'text-gray-600';
    }
  };

  const getEventIcon = (event: string) => {
    switch (event) {
      case 'connected': return <CheckCircle className="w-4 h-4 text-green-500" />;
      case 'disconnected': return <WifiOff className="w-4 h-4 text-red-500" />;
      case 'reconnecting': return <RotateCcw className="w-4 h-4 text-yellow-500 animate-spin" />;
      case 'error': return <AlertTriangle className="w-4 h-4 text-red-500" />;
      default: return <Activity className="w-4 h-4 text-gray-500" />;
    }
  };

  return (
    <div className="p-6 space-y-6 max-h-full overflow-auto">
      <div className="flex items-center justify-between">
        <h1 className="text-2xl font-bold flex items-center gap-2">
          <Globe className="w-6 h-6" />
          Network Status Monitor
        </h1>
        <Button 
          onClick={testConnection}
          disabled={isTestingConnection}
          variant="outline"
          size="sm"
        >
          {isTestingConnection ? (
            <RotateCcw className="w-4 h-4 mr-2 animate-spin" />
          ) : (
            <Zap className="w-4 h-4 mr-2" />
          )}
          Test Connection
        </Button>
      </div>

      {/* Connection Status Overview */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Server className="w-5 h-5" />
            WebSocket Connection
          </CardTitle>
        </CardHeader>
        <CardContent>
          <ConnectionStatus showDetails className="border-0 p-0" />
        </CardContent>
      </Card>

      {/* Network Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Latency</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{metrics.latency}ms</div>
            <p className="text-xs text-gray-500">Round trip time</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Connection Quality</CardTitle>
          </CardHeader>
          <CardContent>
            <div className={`text-2xl font-bold capitalize ${getQualityColor(metrics.connectionQuality)}`}>
              {metrics.connectionQuality}
            </div>
            <p className="text-xs text-gray-500">Overall rating</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Jitter</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{metrics.jitter.toFixed(1)}ms</div>
            <p className="text-xs text-gray-500">Latency variation</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium">Packet Loss</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{metrics.packetLoss.toFixed(1)}%</div>
            <p className="text-xs text-gray-500">Data loss rate</p>
          </CardContent>
        </Card>
      </div>

      {/* Connection History */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Clock className="w-5 h-5" />
            Connection History
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-3 max-h-64 overflow-auto">
            {connectionLogs.length === 0 ? (
              <p className="text-gray-500 text-center py-4">No connection events recorded yet</p>
            ) : (
              connectionLogs.map((log) => (
                <div key={log.id} className="flex items-start gap-3 p-3 rounded-lg bg-gray-50 dark:bg-gray-800">
                  {getEventIcon(log.event)}
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center gap-2 mb-1">
                      <Badge variant={log.event === 'connected' ? 'default' : 
                                   log.event === 'error' ? 'destructive' : 'secondary'}>
                        {log.event}
                      </Badge>
                      <span className="text-xs text-gray-500">
                        {log.timestamp.toLocaleTimeString()}
                      </span>
                    </div>
                    <p className="text-sm text-gray-700 dark:text-gray-300">{log.message}</p>
                    {log.duration && (
                      <p className="text-xs text-gray-500 mt-1">
                        Duration: {Math.round(log.duration / 1000)}s
                      </p>
                    )}
                  </div>
                </div>
              ))
            )}
          </div>
        </CardContent>
      </Card>

      {/* Quick Actions */}
      <Card>
        <CardHeader>
          <CardTitle>Quick Actions</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex gap-2 flex-wrap">
            <Button 
              onClick={() => wsManager.forceReconnect()}
              variant="outline"
              size="sm"
            >
              <RotateCcw className="w-4 h-4 mr-2" />
              Force Reconnect
            </Button>
            <Button 
              onClick={() => wsManager.disconnect()}
              variant="outline"
              size="sm"
            >
              <WifiOff className="w-4 h-4 mr-2" />
              Disconnect
            </Button>
            <Button 
              onClick={() => setConnectionLogs([])}
              variant="outline"
              size="sm"
            >
              Clear History
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

client/src/components/apps/NewsCenter.ts.content:
import { FC, useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { X, Newspaper, RefreshCw, Search, Globe, Clock, ExternalLink } from 'lucide-react';

interface NewsArticle {
  source: {
    id: string | null;
    name: string;
  };
  author: string | null;
  title: string;
  description: string | null;
  url: string;
  urlToImage: string | null;
  publishedAt: string;
  content: string | null;
}

export const NewsCenter: FC = () => {
  const [articles, setArticles] = useState<NewsArticle[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('general');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedArticle, setSelectedArticle] = useState<NewsArticle | null>(null);

  const categories = [
    { id: 'general', name: 'General', icon: 'ðŸ“°' },
    { id: 'business', name: 'Business', icon: 'ðŸ’¼' },
    { id: 'entertainment', name: 'Entertainment', icon: 'ðŸŽ¬' },
    { id: 'health', name: 'Health', icon: 'ðŸ¥' },
    { id: 'science', name: 'Science', icon: 'ðŸ”¬' },
    { id: 'sports', name: 'Sports', icon: 'âš½' },
    { id: 'technology', name: 'Technology', icon: 'ðŸ’»' }
  ];

  useEffect(() => {
    fetchNews();
  }, [selectedCategory]);

  const fetchNews = async () => {
    setLoading(true);
    try {
      const response = await fetch(`/api/news?category=${selectedCategory}`);
      if (response.ok) {
        const data = await response.json();
        setArticles(data);
      } else {
        console.error('Failed to fetch news');
      }
    } catch (error) {
      console.error('News API error:', error);
    }
    setLoading(false);
  };

  const filteredArticles = articles.filter(article =>
    article.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    article.description?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString();
  };

  const getTimeAgo = (dateString: string) => {
    const now = new Date();
    const publishedAt = new Date(dateString);
    const diffMs = now.getTime() - publishedAt.getTime();
    const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
    const diffDays = Math.floor(diffHours / 24);

    if (diffDays > 0) return `${diffDays} day${diffDays > 1 ? 's' : ''} ago`;
    if (diffHours > 0) return `${diffHours} hour${diffHours > 1 ? 's' : ''} ago`;
    return 'Just now';
  };

  return (
    <div className="bg-white rounded-lg shadow-2xl w-full max-w-6xl h-[700px] flex flex-col">
      <div className="bg-red-600 text-white px-4 py-2 flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <Newspaper className="w-5 h-5" />
          <span className="font-medium">News Center</span>
        </div>
        <Button 
          onClick={fetchNews} 
          size="sm" 
          variant="ghost" 
          className="text-white hover:bg-red-700"
          disabled={loading}
        >
          <RefreshCw className={`w-4 h-4 ${loading ? 'animate-spin' : ''}`} />
        </Button>
      </div>

      <div className="flex border-b">
        {categories.map(category => (
          <button
            key={category.id}
            onClick={() => setSelectedCategory(category.id)}
            className={`flex items-center space-x-2 px-4 py-2 ${
              selectedCategory === category.id 
                ? 'bg-red-50 border-b-2 border-red-600 text-red-600' 
                : 'hover:bg-gray-50'
            }`}
          >
            <span>{category.icon}</span>
            <span>{category.name}</span>
          </button>
        ))}
      </div>

      <div className="p-4 border-b">
        <div className="relative">
          <Search className="w-4 h-4 absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
          <Input
            placeholder="Search news articles..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10"
          />
        </div>
      </div>

      <div className="flex flex-1 overflow-hidden">
        <div className="w-2/3 overflow-auto">
          <div className="p-4">
            {loading ? (
              <div className="flex items-center justify-center py-12">
                <RefreshCw className="w-8 h-8 animate-spin text-red-500" />
                <span className="ml-2">Loading latest news...</span>
              </div>
            ) : (
              <div className="space-y-4">
                {filteredArticles.map((article, index) => (
                  <div
                    key={index}
                    onClick={() => setSelectedArticle(article)}
                    className="border rounded-lg p-4 hover:bg-gray-50 cursor-pointer transition-colors"
                  >
                    <div className="flex space-x-4">
                      {article.urlToImage && (
                        <img
                          src={article.urlToImage}
                          alt={article.title}
                          className="w-24 h-24 object-cover rounded"
                          onError={(e) => {
                            (e.target as HTMLImageElement).style.display = 'none';
                          }}
                        />
                      )}
                      <div className="flex-1">
                        <h3 className="font-semibold text-lg mb-2 line-clamp-2">
                          {article.title}
                        </h3>
                        <p className="text-gray-600 text-sm mb-2 line-clamp-2">
                          {article.description}
                        </p>
                        <div className="flex items-center justify-between text-xs text-gray-500">
                          <div className="flex items-center space-x-4">
                            <div className="flex items-center space-x-1">
                              <Globe className="w-3 h-3" />
                              <span>{article.source.name}</span>
                            </div>
                            {article.author && (
                              <span>by {article.author}</span>
                            )}
                          </div>
                          <div className="flex items-center space-x-1">
                            <Clock className="w-3 h-3" />
                            <span>{getTimeAgo(article.publishedAt)}</span>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
                {filteredArticles.length === 0 && !loading && (
                  <div className="text-center py-12 text-gray-500">
                    No articles found matching your search
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        <div className="w-1/3 border-l p-4 overflow-auto">
          {selectedArticle ? (
            <div className="space-y-4">
              {selectedArticle.urlToImage && (
                <img
                  src={selectedArticle.urlToImage}
                  alt={selectedArticle.title}
                  className="w-full h-48 object-cover rounded"
                  onError={(e) => {
                    (e.target as HTMLImageElement).style.display = 'none';
                  }}
                />
              )}

              <div>
                <h2 className="text-xl font-bold mb-2">{selectedArticle.title}</h2>
                <div className="flex items-center justify-between text-sm text-gray-600 mb-4">
                  <div className="flex items-center space-x-1">
                    <Globe className="w-4 h-4" />
                    <span>{selectedArticle.source.name}</span>
                  </div>
                  <div className="flex items-center space-x-1">
                    <Clock className="w-4 h-4" />
                    <span>{getTimeAgo(selectedArticle.publishedAt)}</span>
                  </div>
                </div>
                {selectedArticle.author && (
                  <p className="text-sm text-gray-600 mb-4">
                    By {selectedArticle.author}
                  </p>
                )}
              </div>

              <div className="space-y-3">
                <p className="text-gray-700 leading-relaxed">
                  {selectedArticle.description}
                </p>
                {selectedArticle.content && (
                  <p className="text-gray-700 leading-relaxed">
                    {selectedArticle.content.split('[+')[0]}
                  </p>
                )}
              </div>

              <div className="pt-4 border-t">
                <Button
                  onClick={() => window.open(selectedArticle.url, '_blank')}
                  className="w-full bg-red-600 hover:bg-red-700"
                >
                  <ExternalLink className="w-4 h-4 mr-2" />
                  Read Full Article
                </Button>
              </div>

              <div className="text-xs text-gray-500">
                Published: {formatDate(selectedArticle.publishedAt)}
              </div>
            </div>
          ) : (
            <div className="flex items-center justify-center h-full text-gray-400">
              <div className="text-center">
                <Newspaper className="w-12 h-12 mx-auto mb-4 opacity-50" />
                <p>Select an article to read details</p>
              </div>
            </div>
          )}
        </div>
      </div>

      <div className="bg-gray-100 px-4 py-2 text-sm text-gray-600 border-t">
        Live news powered by NewsAPI â€¢ {filteredArticles.length} articles â€¢ Last updated: {new Date().toLocaleTimeString()}
      </div>
    </div>
  );
};

client/src/components/apps/NotePad.tsx.content:
import { FC, useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Input } from '@/components/ui/input';
import { Save, FileText, Trash2, Plus } from 'lucide-react';

interface Note {
  id: string;
  title: string;
  content: string;
  lastModified: Date;
}

export const NotePad: FC = () => {
  const [notes, setNotes] = useState<Note[]>([]);
  const [activeNote, setActiveNote] = useState<Note | null>(null);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  // Load notes from localStorage
  useEffect(() => {
    const stored = localStorage.getItem('notepad-notes');
    if (stored) {
      const parsedNotes = JSON.parse(stored).map((note: any) => ({
        ...note,
        lastModified: new Date(note.lastModified)
      }));
      setNotes(parsedNotes);
      if (parsedNotes.length > 0) {
        setActiveNote(parsedNotes[0]);
        setTitle(parsedNotes[0].title);
        setContent(parsedNotes[0].content);
      }
    }
  }, []);

  // Save notes to localStorage
  const saveNotes = (updatedNotes: Note[]) => {
    localStorage.setItem('notepad-notes', JSON.stringify(updatedNotes));
    setNotes(updatedNotes);
  };

  const createNewNote = () => {
    const newNote: Note = {
      id: Date.now().toString(),
      title: 'Untitled Note',
      content: '',
      lastModified: new Date()
    };
    const updatedNotes = [newNote, ...notes];
    saveNotes(updatedNotes);
    setActiveNote(newNote);
    setTitle(newNote.title);
    setContent(newNote.content);
  };

  const saveCurrentNote = () => {
    if (!activeNote) return;

    const updatedNote = {
      ...activeNote,
      title: title || 'Untitled Note',
      content,
      lastModified: new Date()
    };

    const updatedNotes = notes.map(note => 
      note.id === activeNote.id ? updatedNote : note
    );

    saveNotes(updatedNotes);
    setActiveNote(updatedNote);
  };

  const deleteNote = (noteId: string) => {
    const updatedNotes = notes.filter(note => note.id !== noteId);
    saveNotes(updatedNotes);

    if (activeNote?.id === noteId) {
      if (updatedNotes.length > 0) {
        setActiveNote(updatedNotes[0]);
        setTitle(updatedNotes[0].title);
        setContent(updatedNotes[0].content);
      } else {
        setActiveNote(null);
        setTitle('');
        setContent('');
      }
    }
  };

  const selectNote = (note: Note) => {
    setActiveNote(note);
    setTitle(note.title);
    setContent(note.content);
  };

  return (
    <div className="h-full flex bg-background">
      {/* Sidebar */}
      <div className="w-64 border-r border-border bg-muted/30">
        <div className="p-3 border-b border-border">
          <Button onClick={createNewNote} className="w-full" size="sm">
            <Plus className="w-4 h-4 mr-2" />
            New Note
          </Button>
        </div>
        <div className="overflow-auto h-full">
          {notes.map((note) => (
            <div
              key={note.id}
              className={`p-3 border-b border-border cursor-pointer hover:bg-accent/50 ${
                activeNote?.id === note.id ? 'bg-accent' : ''
              }`}
              onClick={() => selectNote(note)}
            >
              <div className="flex items-start justify-between">
                <div className="flex-1 min-w-0">
                  <h4 className="font-medium text-sm truncate">{note.title}</h4>
                  <p className="text-xs text-muted-foreground truncate mt-1">
                    {note.content.slice(0, 50)}...
                  </p>
                  <p className="text-xs text-muted-foreground mt-1">
                    {note.lastModified.toLocaleDateString()}
                  </p>
                </div>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={(e) => {
                    e.stopPropagation();
                    deleteNote(note.id);
                  }}
                  className="h-6 w-6 p-0 text-muted-foreground hover:text-destructive"
                >
                  <Trash2 className="w-3 h-3" />
                </Button>
              </div>
            </div>
          ))}
          {notes.length === 0 && (
            <div className="p-6 text-center text-muted-foreground text-sm">
              <FileText className="w-8 h-8 mx-auto mb-2 opacity-50" />
              No notes yet. Create your first note!
            </div>
          )}
        </div>
      </div>

      {/* Editor */}
      <div className="flex-1 flex flex-col">
        {activeNote ? (
          <>
            <div className="p-4 border-b border-border">
              <div className="flex items-center gap-2 mb-3">
                <Input
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  placeholder="Note title..."
                  className="text-lg font-medium"
                />
                <Button onClick={saveCurrentNote} size="sm">
                  <Save className="w-4 h-4 mr-2" />
                  Save
                </Button>
              </div>
            </div>
            <div className="flex-1 p-4">
              <Textarea
                value={content}
                onChange={(e) => setContent(e.target.value)}
                placeholder="Start writing your note..."
                className="w-full h-full resize-none border-none focus:ring-0 text-base"
              />
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center text-muted-foreground">
            <div className="text-center">
              <FileText className="w-16 h-16 mx-auto mb-4 opacity-30" />
              <h3 className="text-lg font-medium mb-2">Welcome to NotePad</h3>
              <p className="text-sm">Create a new note to get started</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

client/src/components/apps/OfficeSuite.t.content:
import { FC, useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { FileSpreadsheet, Monitor, FileText, BarChart3, Calculator, FileImage } from 'lucide-react';
import { Spreadsheet } from './Spreadsheet';
import { PresentationApp } from './Presentation';

type AppType = 'home' | 'spreadsheet' | 'presentation';

export const OfficeSuite: FC = () => {
  const [activeApp, setActiveApp] = useState<AppType>('home');

  const apps = [
    {
      id: 'spreadsheet',
      name: 'Excel',
      description: 'Create and edit spreadsheets with formulas, charts, and data analysis',
      icon: FileSpreadsheet,
      color: 'text-green-600',
      bgColor: 'bg-green-50 hover:bg-green-100',
      component: Spreadsheet
    },
    {
      id: 'presentation',
      name: 'PowerPoint',
      description: 'Design stunning presentations with slides, animations, and multimedia',
      icon: Monitor,
      color: 'text-orange-600',
      bgColor: 'bg-orange-50 hover:bg-orange-100',
      component: PresentationApp
    }
  ];

  if (activeApp !== 'home') {
    const app = apps.find(a => a.id === activeApp);
    if (app) {
      const Component = app.component;
      return (
        <div className="h-full flex flex-col">
          <div className="flex items-center gap-2 p-2 border-b bg-muted/20">
            <Button variant="outline" size="sm" onClick={() => setActiveApp('home')}>
              â† Back to Office
            </Button>
            <div className="flex items-center gap-2">
              <app.icon className={`w-5 h-5 ${app.color}`} />
              <span className="font-medium">Microsoft {app.name}</span>
            </div>
          </div>
          <div className="flex-1">
            <Component />
          </div>
        </div>
      );
    }
  }

  return (
    <div className="h-full flex flex-col bg-gradient-to-br from-blue-50 to-purple-50">
      {/* Header */}
      <div className="p-6 border-b bg-white/80 backdrop-blur-sm">
        <div className="flex items-center gap-3 mb-2">
