import { FC, useState, useRef, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Slider } from '@/components/ui/slider';
import { Input } from '@/components/ui/input';
import { 
  Play, Pause, SkipBack, SkipForward, Volume2, Scissors, 
  Download, Upload, Settings, Eye, Layers, Type, Image,
  Music, Video, Wand2, Undo, Redo, Save, FolderOpen
} from 'lucide-react';

interface VideoClip {
  id: string;
  name: string;
  duration: number;
  startTime: number;
  endTime: number;
  trimStart: number;
  trimEnd: number;
  volume: number;
  effects: string[];
  transitions: string[];
}

interface AudioTrack {
  id: string;
  name: string;
  duration: number;
  volume: number;
  startTime: number;
}

interface TextOverlay {
  id: string;
  text: string;
  startTime: number;
  duration: number;
  style: {
    fontSize: number;
    color: string;
    position: { x: number; y: number };
    fontFamily: string;
  };
}

export const VideoEditor: FC = () => {
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(300); // 5 minutes
  const [isPlaying, setIsPlaying] = useState(false);
  const [zoom, setZoom] = useState(100);
  const [selectedTool, setSelectedTool] = useState('select');
  const [videoClips, setVideoClips] = useState<VideoClip[]>([
    {
      id: '1',
      name: 'intro.mp4',
      duration: 30,
      startTime: 0,
      endTime: 30,
      trimStart: 0,
      trimEnd: 30,
      volume: 100,
      effects: [],
      transitions: []
    },
    {
      id: '2',
      name: 'main_content.mp4',
      duration: 120,
      startTime: 30,
      endTime: 150,
      trimStart: 0,
      trimEnd: 120,
      volume: 100,
      effects: ['color_correction'],
      transitions: ['fade_in']
    }
  ]);

  const [audioTracks, setAudioTracks] = useState<AudioTrack[]>([
    {
      id: '1',
      name: 'background_music.mp3',
      duration: 180,
      volume: 60,
      startTime: 0
    }
  ]);

  const [textOverlays, setTextOverlays] = useState<TextOverlay[]>([
    {
      id: '1',
      text: 'Welcome to WebOS',
      startTime: 5,
      duration: 10,
      style: {
        fontSize: 48,
        color: '#ffffff',
        position: { x: 50, y: 20 },
        fontFamily: 'Arial'
      }
    }
  ]);

  const [projectName, setProjectName] = useState('Untitled Project');
  const videoRef = useRef<HTMLVideoElement>(null);
  const timelineRef = useRef<HTMLDivElement>(null);

  const formatTime = (seconds: number): string => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    const frames = Math.floor((seconds % 1) * 30);
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}:${frames.toString().padStart(2, '0')}`;
  };

  const togglePlayback = () => {
    setIsPlaying(!isPlaying);
  };

  const seekTo = (time: number) => {
    setCurrentTime(Math.max(0, Math.min(time, duration)));
  };

  const exportProject = () => {
    // In a real implementation, this would render the video
    console.log('Exporting project:', {
      videoClips,
      audioTracks,
      textOverlays,
      duration,
      projectName
    });
  };

  const addVideoClip = () => {
    const newClip: VideoClip = {
      id: Date.now().toString(),
      name: `clip_${videoClips.length + 1}.mp4`,
      duration: 60,
      startTime: duration,
      endTime: duration + 60,
      trimStart: 0,
      trimEnd: 60,
      volume: 100,
      effects: [],
      transitions: []
    };
    setVideoClips([...videoClips, newClip]);
    setDuration(duration + 60);
  };

  const addAudioTrack = () => {
    const newTrack: AudioTrack = {
      id: Date.now().toString(),
      name: `audio_${audioTracks.length + 1}.mp3`,
      duration: 120,
      volume: 80,
      startTime: 0
    };
    setAudioTracks([...audioTracks, newTrack]);
  };

  const addTextOverlay = () => {
    const newText: TextOverlay = {
      id: Date.now().toString(),
      text: 'New Text',
      startTime: currentTime,
      duration: 5,
      style: {
        fontSize: 32,
        color: '#ffffff',
        position: { x: 50, y: 50 },
        fontFamily: 'Arial'
      }
    };
    setTextOverlays([...textOverlays, newText]);
  };

  // Playback simulation
  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isPlaying) {
      interval = setInterval(() => {
        setCurrentTime(prev => {
          if (prev >= duration) {
            setIsPlaying(false);
            return duration;
          }
          return prev + 0.1;
        });
      }, 100);
    }
    return () => clearInterval(interval);
  }, [isPlaying, duration]);

  const timelineScale = zoom / 100;
  const timelineWidth = duration * 10 * timelineScale;

  return (
    <div className="h-full flex flex-col bg-gray-900 text-white">
      {/* Header */}
      <div className="flex items-center justify-between p-4 bg-gray-800 border-b border-gray-700">
        <div className="flex items-center gap-4">
          <Video className="w-6 h-6 text-blue-400" />
          <Input
            value={projectName}
            onChange={(e) => setProjectName(e.target.value)}
            className="bg-gray-700 border-gray-600 text-white w-48"
          />
        </div>

        <div className="flex items-center gap-2">
          <Button variant="outline" size="sm" className="text-white border-gray-600">
            <FolderOpen className="w-4 h-4 mr-1" />
            Open
          </Button>
          <Button variant="outline" size="sm" className="text-white border-gray-600">
            <Save className="w-4 h-4 mr-1" />
            Save
          </Button>
          <Button size="sm" className="bg-blue-600 hover:bg-blue-700">
            <Download className="w-4 h-4 mr-1" />
            Export
          </Button>
        </div>
      </div>

      <div className="flex-1 flex">
        {/* Toolbar */}
        <div className="w-16 bg-gray-800 border-r border-gray-700 flex flex-col items-center py-4 gap-2">
          {[
            { id: 'select', icon: Eye, label: 'Select' },
            { id: 'cut', icon: Scissors, label: 'Cut' },
            { id: 'text', icon: Type, label: 'Text' },
            { id: 'image', icon: Image, label: 'Image' },
            { id: 'effects', icon: Wand2, label: 'Effects' }
          ].map(tool => (
            <Button
              key={tool.id}
              variant={selectedTool === tool.id ? 'default' : 'ghost'}
              size="sm"
              className="w-10 h-10 p-0"
              onClick={() => setSelectedTool(tool.id)}
              title={tool.label}
            >
              <tool.icon className="w-4 h-4" />
            </Button>
          ))}
        </div>

        {/* Main Content */}
        <div className="flex-1 flex flex-col">
          {/* Preview Area */}
          <div className="flex-1 flex">
            {/* Video Preview */}
            <div className="flex-1 bg-black flex items-center justify-center relative">
              <div className="w-full max-w-2xl aspect-video bg-gray-800 rounded-lg flex items-center justify-center relative overflow-hidden">
                {/* Video placeholder */}
                <div className="absolute inset-0 bg-gradient-to-br from-blue-600 to-purple-600 opacity-50"></div>

                {/* Text overlays */}
                {textOverlays.map(overlay => {
                  const isVisible = currentTime >= overlay.startTime && currentTime <= overlay.startTime + overlay.duration;
                  return isVisible ? (
                    <div
                      key={overlay.id}
                      className="absolute text-white font-bold pointer-events-none"
                      style={{
                        left: `${overlay.style.position.x}%`,
                        top: `${overlay.style.position.y}%`,
                        fontSize: `${overlay.style.fontSize}px`,
                        color: overlay.style.color,
                        fontFamily: overlay.style.fontFamily,
                        transform: 'translate(-50%, -50%)'
                      }}
                    >
                      {overlay.text}
                    </div>
                  ) : null;
                })}

                {/* Playback indicator */}
                <div className="absolute bottom-4 left-4 bg-black/70 px-2 py-1 rounded text-sm">
                  {formatTime(currentTime)} / {formatTime(duration)}
                </div>
              </div>
            </div>

            {/* Properties Panel */}
            <div className="w-80 bg-gray-800 border-l border-gray-700">
              <Tabs defaultValue="video" className="h-full">
                <TabsList className="grid w-full grid-cols-3 bg-gray-700">
                  <TabsTrigger value="video">Video</TabsTrigger>
                  <TabsTrigger value="audio">Audio</TabsTrigger>
                  <TabsTrigger value="effects">Effects</TabsTrigger>
                </TabsList>

                <TabsContent value="video" className="p-4 space-y-4">
                  <Card className="bg-gray-700 border-gray-600">
                    <CardHeader className="pb-2">
                      <CardTitle className="text-sm">Video Properties</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-3">
                      <div>
                        <label className="text-xs text-gray-400">Resolution</label>
                        <select className="w-full bg-gray-600 border border-gray-500 rounded px-2 py-1 text-sm">
                          <option>1920x1080 (Full HD)</option>
                          <option>1280x720 (HD)</option>
                          <option>3840x2160 (4K)</option>
                        </select>
                      </div>
                      <div>
                        <label className="text-xs text-gray-400">Frame Rate</label>
                        <select className="w-full bg-gray-600 border border-gray-500 rounded px-2 py-1 text-sm">
                          <option>30 fps</option>
                          <option>60 fps</option>
                          <option>24 fps</option>
                        </select>
                      </div>
                      <div>
                        <label className="text-xs text-gray-400">Brightness</label>
                        <Slider defaultValue={[0]} min={-100} max={100} step={1} className="mt-1" />
                      </div>
                      <div>
                        <label className="text-xs text-gray-400">Contrast</label>
                        <Slider defaultValue={[0]} min={-100} max={100} step={1} className="mt-1" />
                      </div>
                      <div>
                        <label className="text-xs text-gray-400">Saturation</label>
                        <Slider defaultValue={[0]} min={-100} max={100} step={1} className="mt-1" />
                      </div>
                    </CardContent>
                  </Card>
                </TabsContent>

                <TabsContent value="audio" className="p-4 space-y-4">
                  <Card className="bg-gray-700 border-gray-600">
                    <CardHeader className="pb-2">
                      <CardTitle className="text-sm">Audio Mixer</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-3">
                      <div>
                        <label className="text-xs text-gray-400">Master Volume</label>
                        <Slider defaultValue={[100]} min={0} max={100} step={1} className="mt-1" />
                      </div>
                      {audioTracks.map(track => (
                        <div key={track.id} className="p-2 bg-gray-600 rounded">
                          <div className="text-xs font-medium mb-1">{track.name}</div>
                          <Slider defaultValue={[track.volume]} min={0} max={100} step={1} />
                        </div>
                      ))}
                      <Button size="sm" className="w-full" onClick={addAudioTrack}>
                        <Music className="w-4 h-4 mr-1" />
                        Add Audio Track
                      </Button>
                    </CardContent>
                  </Card>
                </TabsContent>

                <TabsContent value="effects" className="p-4 space-y-4">
                  <Card className="bg-gray-700 border-gray-600">
                    <CardHeader className="pb-2">
                      <CardTitle className="text-sm">Effects Library</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-2">
                      {[
                        'Blur', 'Sharpen', 'Color Grade', 'Film Grain', 
                        'Vignette', 'Chromatic Aberration', 'Lens Flare', 'Glow'
                      ].map(effect => (
                        <Button
                          key={effect}
                          variant="outline"
                          size="sm"
                          className="w-full justify-start text-white border-gray-600"
                        >
                          {effect}
                        </Button>
                      ))}
                    </CardContent>
                  </Card>

                  <Card className="bg-gray-700 border-gray-600">
                    <CardHeader className="pb-2">
                      <CardTitle className="text-sm">Transitions</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-2">
                      {[
                        'Cut', 'Fade', 'Dissolve', 'Wipe', 'Slide', 'Zoom'
                      ].map(transition => (
                        <Button
                          key={transition}
                          variant="outline"
                          size="sm"
                          className="w-full justify-start text-white border-gray-600"
                        >
                          {transition}
                        </Button>
                      ))}
                    </CardContent>
                  </Card>
                </TabsContent>
              </Tabs>
            </div>
          </div>

          {/* Timeline */}
          <div className="h-64 bg-gray-800 border-t border-gray-700 flex flex-col">
            {/* Timeline Controls */}
            <div className="flex items-center justify-between p-2 bg-gray-700 border-b border-gray-600">
              <div className="flex items-center gap-2">
                <Button size="sm" variant="ghost" onClick={togglePlayback}>
                  {isPlaying ? <Pause className="w-4 h-4" /> : <Play className="w-4 h-4" />}
                </Button>
                <Button size="sm" variant="ghost" onClick={() => seekTo(0)}>
                  <SkipBack className="w-4 h-4" />
                </Button>
                <Button size="sm" variant="ghost" onClick={() => seekTo(duration)}>
                  <SkipForward className="w-4 h-4" />
                </Button>
                <div className="text-sm font-mono">
                  {formatTime(currentTime)}
                </div>
              </div>

              <div className="flex items-center gap-2">
                <Button size="sm" variant="ghost" onClick={addVideoClip}>
                  <Upload className="w-4 h-4 mr-1" />
                  Add Media
                </Button>
                <Button size="sm" variant="ghost" onClick={addTextOverlay}>
                  <Type className="w-4 h-4 mr-1" />
                  Add Text
                </Button>
                <div className="flex items-center gap-1">
                  <label className="text-xs">Zoom:</label>
                  <Slider
                    value={[zoom]}
                    onValueChange={(value) => setZoom(value[0])}
                    min={25}
                    max={400}
                    step={25}
                    className="w-16"
                  />
                  <span className="text-xs w-8">{zoom}%</span>
                </div>
              </div>
            </div>

            {/* Timeline Tracks */}
            <div className="flex-1 overflow-auto" ref={timelineRef}>
              <div className="relative" style={{ width: Math.max(800, timelineWidth) }}>
                {/* Time ruler */}
                <div className="h-6 bg-gray-600 border-b border-gray-500 relative">
                  {Array.from({ length: Math.ceil(duration / 10) + 1 }, (_, i) => (
                    <div
                      key={i}
                      className="absolute top-0 bottom-0 border-l border-gray-400"
                      style={{ left: i * 100 * timelineScale }}
                    >
                      <span className="text-xs text-gray-300 ml-1">{i * 10}s</span>
                    </div>
                  ))}

                  {/* Playhead */}
                  <div
                    className="absolute top-0 bottom-0 w-0.5 bg-red-500 z-10"
                    style={{ left: currentTime * 10 * timelineScale }}
                  />
                </div>

                {/* Video Track */}
                <div className="h-12 bg-gray-700 border-b border-gray-600 relative">
                  <div className="absolute left-0 top-0 h-full flex items-center px-2 text-xs font-medium bg-gray-600 border-r border-gray-500 w-16">
                    Video 1
                  </div>
                  {videoClips.map(clip => (
                    <div
                      key={clip.id}
                      className="absolute top-1 bottom-1 bg-blue-600 rounded border border-blue-400 cursor-pointer hover:bg-blue-500"
                      style={{
                        left: clip.startTime * 10 * timelineScale + 64,
                        width: (clip.endTime - clip.startTime) * 10 * timelineScale
                      }}
                    >
                      <div className="p-1 text-xs text-white truncate">{clip.name}</div>
                    </div>
                  ))}
                </div>

                {/* Audio Track */}
                <div className="h-12 bg-gray-700 border-b border-gray-600 relative">
                  <div className="absolute left-0 top-0 h-full flex items-center px-2 text-xs font-medium bg-gray-600 border-r border-gray-500 w-16">
                    Audio 1
                  </div>
                  {audioTracks.map(track => (
                    <div
                      key={track.id}
                      className="absolute top-1 bottom-1 bg-green-600 rounded border border-green-400 cursor-pointer hover:bg-green-500"
                      style={{
                        left: track.startTime * 10 * timelineScale + 64,
                        width: track.duration * 10 * timelineScale
                      }}
                    >
                      <div className="p-1 text-xs text-white truncate">{track.name}</div>
                    </div>
                  ))}
                </div>

                {/* Text Track */}
                <div className="h-12 bg-gray-700 border-b border-gray-600 relative">
                  <div className="absolute left-0 top-0 h-full flex items-center px-2 text-xs font-medium bg-gray-600 border-r border-gray-500 w-16">
                    Text
                  </div>
                  {textOverlays.map(overlay => (
                    <div
                      key={overlay.id}
                      className="absolute top-1 bottom-1 bg-yellow-600 rounded border border-yellow-400 cursor-pointer hover:bg-yellow-500"
                      style={{
                        left: overlay.startTime * 10 * timelineScale + 64,
                        width: overlay.duration * 10 * timelineScale
                      }}
                    >
                      <div className="p-1 text-xs text-white truncate">{overlay.text}</div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

client/src/components/apps/VirtualMachin.content:
import { FC, useState, useEffect, useRef } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { 
  Play, Pause, Square, RotateCcw, Settings, Monitor, Cpu, 
  HardDrive, Zap, Wifi, Download, Upload, Terminal,
  Power, PowerOff, RefreshCw, Volume2, VolumeX, 
  Maximize2, Minimize2, MoreVertical, ChevronRight
} from 'lucide-react';

interface VirtualMachine {
  id: string;
  name: string;
  os: string;
  status: 'stopped' | 'running' | 'paused' | 'booting';
  cpu: number;
  memory: number;
  storage: number;
  uptime: number;
  created: Date;
  lastUsed: Date;
}

interface SystemStats {
  cpu: number;
  memory: number;
  disk: number;
  network: { upload: number; download: number };
}

export const VirtualMachine: FC = () => {
  const [currentTab, setCurrentTab] = useState('dashboard');
  const [selectedVM, setSelectedVM] = useState<string | null>(null);
  const [isCreating, setIsCreating] = useState(false);
  const [systemStats, setSystemStats] = useState<SystemStats>({
    cpu: 0,
    memory: 0,
    disk: 0,
    network: { upload: 0, download: 0 }
  });
  const [bootSequence, setBootSequence] = useState<string[]>([]);
  const [isBooting, setIsBooting] = useState(false);
  const canvasRef = useRef<HTMLCanvasElement>(null);

  const [virtualMachines, setVirtualMachines] = useState<VirtualMachine[]>([
    {
      id: 'vm-1',
      name: 'Ubuntu Desktop 24.04',
      os: 'ubuntu',
      status: 'stopped',
      cpu: 2,
      memory: 4096,
      storage: 25600,
      uptime: 0,
      created: new Date('2024-11-15'),
      lastUsed: new Date('2024-12-30')
    },
    {
      id: 'vm-2',
      name: 'Windows 11 Pro',
      os: 'windows',
      status: 'stopped',
      cpu: 4,
      memory: 8192,
      storage: 51200,
      uptime: 0,
      created: new Date('2024-11-20'),
      lastUsed: new Date('2024-12-28')
    },
    {
      id: 'vm-3',
      name: 'macOS Sonoma',
      os: 'macos',
      status: 'running',
      cpu: 4,
      memory: 8192,
      storage: 76800,
      uptime: 2847,
      created: new Date('2024-11-25'),
      lastUsed: new Date()
    },
    {
      id: 'vm-4',
      name: 'Alpine Linux',
      os: 'alpine',
      status: 'paused',
      cpu: 1,
      memory: 512,
      storage: 2048,
      uptime: 156,
      created: new Date('2024-12-01'),
      lastUsed: new Date('2024-12-29')
    }
  ]);

  // Simulate system stats updates
  useEffect(() => {
    const interval = setInterval(() => {
      setSystemStats(prev => ({
        cpu: Math.random() * 100,
        memory: Math.random() * 100,
        disk: Math.random() * 100,
        network: {
          upload: Math.random() * 1000,
          download: Math.random() * 5000
        }
      }));
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  // Update VM uptimes
  useEffect(() => {
    const interval = setInterval(() => {
      setVirtualMachines(prev => prev.map(vm => 
        vm.status === 'running' 
          ? { ...vm, uptime: vm.uptime + 1 }
          : vm
      ));
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const getOSIcon = (os: string) => {
    switch (os) {
      case 'ubuntu': return 'ðŸ§';
      case 'windows': return 'ðŸªŸ';
      case 'macos': return 'ðŸŽ';
      case 'alpine': return 'ðŸ”ï¸';
      default: return 'ðŸ’»';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'running': return 'bg-green-500';
      case 'paused': return 'bg-yellow-500';
      case 'booting': return 'bg-blue-500';
      default: return 'bg-gray-500';
    }
  };

  const formatUptime = (seconds: number) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;
    return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const formatBytes = (bytes: number) => {
    const sizes = ['B', 'KB', 'MB', 'GB', 'TB'];
    if (bytes === 0) return '0 B';
    const i = Math.floor(Math.log(bytes) / Math.log(1024));
    return Math.round((bytes / Math.pow(1024, i)) * 100) / 100 + ' ' + sizes[i];
  };

  const startVM = async (vmId: string) => {
    const vm = virtualMachines.find(v => v.id === vmId);
    if (!vm) return;

    setIsBooting(true);
    setBootSequence([]);

    // Simulate boot sequence
    const bootMessages = [
      'BIOS v2.1.0 - WebOS Virtual Machine',
      'Checking system memory... OK',
      'Initializing CPU cores... OK',
      'Loading virtual hardware drivers...',
      'Starting disk check...',
      `Booting ${vm.name}...`,
      'Loading kernel modules...',
      'Mounting file systems...',
      'Starting system services...',
      'Initializing network interfaces...',
      'Boot sequence complete!'
    ];

    for (let i = 0; i < bootMessages.length; i++) {
      await new Promise(resolve => setTimeout(resolve, 800));
      setBootSequence(prev => [...prev, bootMessages[i]]);
    }

    setVirtualMachines(prev => prev.map(v =>
      v.id === vmId 
        ? { ...v, status: 'running', lastUsed: new Date(), uptime: 0 }
        : v
    ));

    setIsBooting(false);
    setSelectedVM(vmId);
  };

  const stopVM = (vmId: string) => {
    setVirtualMachines(prev => prev.map(v =>
      v.id === vmId 
        ? { ...v, status: 'stopped', uptime: 0 }
        : v
    ));
  };

  const pauseVM = (vmId: string) => {
    setVirtualMachines(prev => prev.map(v =>
      v.id === vmId 
        ? { ...v, status: v.status === 'paused' ? 'running' : 'paused' }
        : v
    ));
  };

  const resetVM = (vmId: string) => {
    stopVM(vmId);
    setTimeout(() => startVM(vmId), 1000);
  };

  // Virtual screen rendering
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const renderScreen = () => {
      // Clear screen
      ctx.fillStyle = '#000';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      if (selectedVM) {
        const vm = virtualMachines.find(v => v.id === selectedVM);
        if (vm && vm.status === 'running') {
          // Render desktop environment
          ctx.fillStyle = '#1e40af';
          ctx.fillRect(0, 0, canvas.width, 30); // Title bar

          ctx.fillStyle = '#fff';
          ctx.font = '12px monospace';
          ctx.fillText(`${vm.name} - Virtual Desktop`, 10, 20);

          // Render desktop
          ctx.fillStyle = '#3b82f6';
          ctx.fillRect(0, 30, canvas.width, canvas.height - 30);

          // Add some desktop icons
          const icons = ['ðŸ“ Documents', 'ðŸ–¼ï¸ Pictures', 'âš™ï¸ Settings', 'ðŸ”§ Terminal'];
          icons.forEach((icon, i) => {
            const x = 20 + (i % 4) * 100;
            const y = 60 + Math.floor(i / 4) * 80;

            ctx.fillStyle = '#1e40af';
            ctx.fillRect(x, y, 80, 60);
            ctx.fillStyle = '#fff';
            ctx.font = '10px sans-serif';
            ctx.fillText(icon, x + 5, y + 40);
          });

          // Add time display
          ctx.fillStyle = '#fff';
          ctx.font = '14px monospace';
          const time = new Date().toLocaleTimeString();
          ctx.fillText(time, canvas.width - 100, 20);
        }
      }

      if (isBooting) {
        // Boot screen
        ctx.fillStyle = '#000';
        ctx.fillRect(0, 0, canvas.width, canvas.height);

        ctx.fillStyle = '#00ff00';
        ctx.font = '12px monospace';
        bootSequence.forEach((message, i) => {
          ctx.fillText(message, 10, 20 + i * 16);
        });

        // Add cursor
        const cursorY = 20 + bootSequence.length * 16;
        ctx.fillText('_', 10, cursorY);
      }
    };

    const animationFrame = requestAnimationFrame(renderScreen);
    return () => cancelAnimationFrame(animationFrame);
  }, [selectedVM, virtualMachines, isBooting, bootSequence]);

  return (
    <div className="h-full bg-gray-900 text-white">
      <Tabs value={currentTab} onValueChange={setCurrentTab} className="h-full flex flex-col">
        <div className="bg-gray-800 border-b border-gray-700 p-4">
          <div className="flex items-center justify-between mb-4">
            <h1 className="text-2xl font-bold">Virtual Machine Manager</h1>
            <div className="flex items-center gap-2">
              <Badge variant="outline" className="text-green-400 border-green-400">
                {virtualMachines.filter(vm => vm.status === 'running').length} Running
              </Badge>
              <Badge variant="outline" className="text-yellow-400 border-yellow-400">
                {virtualMachines.filter(vm => vm.status === 'paused').length} Paused
              </Badge>
              <Badge variant="outline" className="text-gray-400 border-gray-400">
                {virtualMachines.filter(vm => vm.status === 'stopped').length} Stopped
              </Badge>
            </div>
          </div>

          <TabsList className="bg-gray-700">
            <TabsTrigger value="dashboard" className="data-[state=active]:bg-gray-600">
              <Monitor className="w-4 h-4 mr-1" />
              Dashboard
            </TabsTrigger>
            <TabsTrigger value="console" className="data-[state=active]:bg-gray-600">
              <Terminal className="w-4 h-4 mr-1" />
              Console
            </TabsTrigger>
            <TabsTrigger value="settings" className="data-[state=active]:bg-gray-600">
              <Settings className="w-4 h-4 mr-1" />
              Settings
            </TabsTrigger>
          </TabsList>
        </div>

        <div className="flex-1 overflow-auto">
          <TabsContent value="dashboard" className="p-6 space-y-6">
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
              {/* VM List */}
              <div className="lg:col-span-2">
                <Card className="bg-gray-800 border-gray-700">
                  <CardHeader>
                    <CardTitle className="text-white flex items-center justify-between">
                      Virtual Machines
                      <Button size="sm" className="bg-blue-600 hover:bg-blue-700">
                        <Play className="w-4 h-4 mr-1" />
                        Create New
                      </Button>
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-4">
                      {virtualMachines.map((vm) => (
                        <Card key={vm.id} className="bg-gray-700 border-gray-600 hover:bg-gray-650 transition-colors">
                          <CardContent className="p-4">
                            <div className="flex items-center justify-between">
                              <div className="flex items-center gap-3">
                                <div className="text-2xl">{getOSIcon(vm.os)}</div>
                                <div>
                                  <h3 className="font-medium text-white">{vm.name}</h3>
                                  <div className="flex items-center gap-2 text-sm text-gray-400">
                                    <div className={`w-2 h-2 rounded-full ${getStatusColor(vm.status)}`}></div>
                                    <span className="capitalize">{vm.status}</span>
                                    {vm.status === 'running' && (
                                      <span>â€¢ Uptime: {formatUptime(vm.uptime)}</span>
                                    )}
                                  </div>
                                </div>
                              </div>

                              <div className="flex items-center gap-2">
                                <div className="text-right text-sm text-gray-400 mr-4">
                                  <div>{vm.cpu} CPU â€¢ {formatBytes(vm.memory * 1024 * 1024)}</div>
                                  <div>{formatBytes(vm.storage * 1024 * 1024)} Storage</div>
                                </div>

                                <div className="flex gap-1">
                                  {vm.status === 'stopped' ? (
                                    <Button
                                      size="sm"
                                      variant="outline"
                                      className="border-green-600 text-green-400 hover:bg-green-600"
                                      onClick={() => startVM(vm.id)}
                                    >
                                      <Play className="w-4 h-4" />
                                    </Button>
                                  ) : (
                                    <>
                                      <Button
                                        size="sm"
                                        variant="outline"
                                        className="border-yellow-600 text-yellow-400 hover:bg-yellow-600"
                                        onClick={() => pauseVM(vm.id)}
                                      >
                                        {vm.status === 'paused' ? <Play className="w-4 h-4" /> : <Pause className="w-4 h-4" />}
                                      </Button>
                                      <Button
                                        size="sm"
                                        variant="outline"
                                        className="border-red-600 text-red-400 hover:bg-red-600"
                                        onClick={() => stopVM(vm.id)}
                                      >
                                        <Square className="w-4 h-4" />
                                      </Button>
                                    </>
                                  )}
                                  <Button
                                    size="sm"
                                    variant="outline"
                                    className="border-blue-600 text-blue-400 hover:bg-blue-600"
                                    onClick={() => resetVM(vm.id)}
                                  >
                                    <RotateCcw className="w-4 h-4" />
                                  </Button>
                                </div>
                              </div>
                            </div>
                          </CardContent>
                        </Card>
                      ))}
                    </div>
                  </CardContent>
                </Card>
              </div>

              {/* System Stats */}
              <div>
                <Card className="bg-gray-800 border-gray-700">
                  <CardHeader>
                    <CardTitle className="text-white">System Resources</CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    <div>
                      <div className="flex justify-between text-sm mb-1">
                        <span className="text-gray-400">CPU Usage</span>
                        <span className="text-white">{systemStats.cpu.toFixed(1)}%</span>
                      </div>
                      <Progress value={systemStats.cpu} className="h-2" />
                    </div>

                    <div>
                      <div className="flex justify-between text-sm mb-1">
                        <span className="text-gray-400">Memory</span>
                        <span className="text-white">{systemStats.memory.toFixed(1)}%</span>
                      </div>
                      <Progress value={systemStats.memory} className="h-2" />
                    </div>

                    <div>
                      <div className="flex justify-between text-sm mb-1">
                        <span className="text-gray-400">Disk I/O</span>
                        <span className="text-white">{systemStats.disk.toFixed(1)}%</span>
                      </div>
                      <Progress value={systemStats.disk} className="h-2" />
                    </div>

                    <div className="border-t border-gray-600 pt-4">
                      <h4 className="text-sm font-medium text-white mb-2">Network Activity</h4>
                      <div className="space-y-2 text-sm">
                        <div className="flex justify-between">
                          <span className="text-gray-400">Upload</span>
                          <span className="text-green-400">{formatBytes(systemStats.network.upload)}/s</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-gray-400">Download</span>
                          <span className="text-blue-400">{formatBytes(systemStats.network.download)}/s</span>
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              </div>
            </div>
          </TabsContent>

          <TabsContent value="console" className="p-6">
            <Card className="bg-gray-800 border-gray-700">
              <CardHeader>
                <CardTitle className="text-white flex items-center justify-between">
                  Virtual Console
                  {selectedVM && (
                    <div className="flex items-center gap-2">
                      <Badge variant="outline" className="text-blue-400 border-blue-400">
                        {virtualMachines.find(vm => vm.id === selectedVM)?.name}
                      </Badge>
                      <Button size="sm" variant="outline">
                        <Maximize2 className="w-4 h-4" />
                      </Button>
                    </div>
                  )}
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="relative">
                  <canvas
                    ref={canvasRef}
                    width={800}
                    height={500}
                    className="w-full bg-black border border-gray-600 rounded"
                  />
                  {!selectedVM && !isBooting && (
                    <div className="absolute inset-0 flex items-center justify-center bg-black/50 rounded">
                      <div className="text-center text-gray-400">
                        <Monitor className="w-12 h-12 mx-auto mb-4 opacity-50" />
                        <p>Select a running VM to view console</p>
                      </div>
                    </div>
                  )}
                </div>

                <div className="flex justify-center gap-2 mt-4">
                  <Button size="sm" variant="outline" className="border-gray-600">
                    <Volume2 className="w-4 h-4 mr-1" />
                    Audio
                  </Button>
                  <Button size="sm" variant="outline" className="border-gray-600">
                    <Wifi className="w-4 h-4 mr-1" />
                    Network
                  </Button>
                  <Button size="sm" variant="outline" className="border-gray-600">
                    <HardDrive className="w-4 h-4 mr-1" />
                    Storage
                  </Button>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="settings" className="p-6">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <Card className="bg-gray-800 border-gray-700">
                <CardHeader>
                  <CardTitle className="text-white">Hypervisor Settings</CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      Default CPU Cores
                    </label>
                    <Select defaultValue="2">
                      <SelectTrigger className="bg-gray-700 border-gray-600">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent className="bg-gray-700 border-gray-600">
                        <SelectItem value="1">1 Core</SelectItem>
                        <SelectItem value="2">2 Cores</SelectItem>
                        <SelectItem value="4">4 Cores</SelectItem>
                        <SelectItem value="8">8 Cores</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      Default RAM (MB)
                    </label>
                    <Select defaultValue="4096">
                      <SelectTrigger className="bg-gray-700 border-gray-600">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent className="bg-gray-700 border-gray-600">
                        <SelectItem value="512">512 MB</SelectItem>
                        <SelectItem value="1024">1 GB</SelectItem>
                        <SelectItem value="2048">2 GB</SelectItem>
                        <SelectItem value="4096">4 GB</SelectItem>
                        <SelectItem value="8192">8 GB</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      Storage Path
                    </label>
                    <Input 
                      defaultValue="/home/user/VirtualMachines"
                      className="bg-gray-700 border-gray-600 text-white"
                    />
                  </div>
                </CardContent>
              </Card>

              <Card className="bg-gray-800 border-gray-700">
                <CardHeader>
                  <CardTitle className="text-white">Network Configuration</CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      Network Mode
                    </label>
                    <Select defaultValue="nat">
                      <SelectTrigger className="bg-gray-700 border-gray-600">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent className="bg-gray-700 border-gray-600">
                        <SelectItem value="nat">NAT</SelectItem>
                        <SelectItem value="bridge">Bridged</SelectItem>
                        <SelectItem value="internal">Internal</SelectItem>
                        <SelectItem value="host">Host-only</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      Virtual Network
                    </label>
                    <Input 
                      defaultValue="192.168.1.0/24"
                      className="bg-gray-700 border-gray-600 text-white"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      DHCP Range
                    </label>
                    <Input 
                      defaultValue="192.168.1.100-200"
                      className="bg-gray-700 border-gray-600 text-white"
                    />
                  </div>
                </CardContent>
              </Card>
            </div>
          </TabsContent>
        </div>
      </Tabs>
    </div>
  );
};

client/src/components/apps/WeatherApp.ts.content:
import { FC, useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardHeader, CardContent, CardTitle } from '@/components/ui/card';
import { CloudSun, Search, Thermometer, Droplets, Wind, Eye } from 'lucide-react';

export const WeatherApp: FC = () => {
  const [city, setCity] = useState('New York');
  const [weather, setWeather] = useState<any>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchWeather = async (cityName: string) => {
    setLoading(true);
    setError('');

    try {
      // This would need an API key from the user for real weather data
      // For now, show a realistic interface with sample data
      await new Promise(resolve => setTimeout(resolve, 1000)); // Simulate API call

      setWeather({
        location: cityName,
        temperature: Math.floor(Math.random() * 30) + 5,
        description: ['Sunny', 'Partly Cloudy', 'Cloudy', 'Light Rain'][Math.floor(Math.random() * 4)],
        humidity: Math.floor(Math.random() * 40) + 40,
        windSpeed: Math.floor(Math.random() * 20) + 5,
        visibility: Math.floor(Math.random() * 10) + 5,
        feelsLike: Math.floor(Math.random() * 35) + 0
      });
    } catch (err) {
      setError('Weather service unavailable. Please provide API key for real data.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchWeather(city);
  }, []);

  const handleSearch = () => {
    if (city.trim()) {
      fetchWeather(city);
    }
  };

  return (
    <div className="h-full bg-gradient-to-br from-blue-400 to-blue-600 p-6 overflow-auto">
      <div className="max-w-2xl mx-auto">
        <div className="flex items-center gap-3 mb-6">
          <CloudSun className="w-8 h-8 text-white" />
          <h1 className="text-2xl font-bold text-white">Weather App</h1>
        </div>

        <div className="flex gap-2 mb-6">
          <Input
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder="Enter city name..."
            className="bg-white/20 border-white/30 text-white placeholder:text-white/70"
            onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
          />
          <Button 
            onClick={handleSearch} 
            disabled={loading}
            className="bg-white/20 hover:bg-white/30 text-white border-white/30"
          >
            <Search className="w-4 h-4" />
          </Button>
        </div>

        {error && (
          <Card className="mb-6 bg-red-500/20 border-red-300">
            <CardContent className="p-4">
              <p className="text-white">{error}</p>
            </CardContent>
          </Card>
        )}

        {loading && (
          <Card className="bg-white/10 border-white/20">
            <CardContent className="p-6 text-center">
              <div className="animate-spin w-8 h-8 border-2 border-white/30 border-t-white rounded-full mx-auto mb-4"></div>
              <p className="text-white">Loading weather data...</p>
            </CardContent>
          </Card>
        )}

        {weather && !loading && (
          <div className="space-y-4">
            <Card className="bg-white/10 border-white/20">
